hudson.tasks.BatchFile.command:
@echo off

setlocal

"%SEVENZ%" x -aoa AMDAPPSDKROOT\%AMDAPPSDKROOT_VERSION%\AMDAPPSDKROOT.7z -o%WORKSPACE%\AMDAPPSDKROOT -xr!docs -xr!bin
if NOT ERRORLEVEL 0 (
	exit /b ERRORLEVEL
)

echo [SUCCESS]Done extracting the AMDAPPSDKROOT, proceeding to copy APARAPI\updated\build.* to com.amd.aparapi.jni
copy /V /Y APARAPI\updated\build.* com.amd.aparapi.jni
if NOT ERRORLEVEL 0 (
	exit /b ERRORLEVEL
)

echo [SUCCESS]Done copying APARAPI\updated\build.* to com.amd.aparapi.jni

:: Successfully copied the build.* files to the com.and.aparapi.jni folder
:: Now based on the version of Visual Studio being used
:: Retrieve the Visual Studio Install Directory
set VSKEY=
if "%T%"=="v120" (
	set VSKEY=12.0
)
if "%T%"=="v110" (
	set VSKEY=11.0
)
if "%T%"=="v100" (
	set VSKEY=10.0
)

set KEY="HKLM\SOFTWARE\Microsoft\VisualStudio\%VSKEY%"
if "%P%"=="amd64" (
	set KEY="HKLM\SOFTWARE\Wow6432Node\Microsoft\VisualStudio\%VSKEY%"
)

echo KEY: %KEY%
set VALUE=ShellFolder

reg query %KEY% /v %VALUE% 2>nul || (
	echo %T tools not installed
	exit /b 1
)
for /f "tokens=2,*" %%a in ('reg query %KEY% /v %VALUE% ^| findstr %VALUE%') do (
	set MSVC.DIR=%%b
)

echo MSVC.DIR: %MSVC.DIR%
echo MSVC.DIR=%MSVC.DIR% > Environ.props
echo AMDAPPSDKROOT=%WORKSPACE%\AMDAPPSDKROOT >> Environ.props

endlocal
