def map = [:] ;

if ( JOB_NAME.contains( "v120" ) ) {
	map.put( "VSKEY", "12.0" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	map.put( "VSKEY", "11.0" );
}

if( JOB_NAME.contains( "x86" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x86" );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86" );
	map.put( "PLATFORM_ARCH_ALT", "32" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "PLATFORM_ARCH_VS", "Win32");
}

if( JOB_NAME.contains( "amd64" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x64" );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86_64" );
	map.put( "PLATFORM_ARCH_ALT", "64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "PLATFORM_ARCH_VS", "amd64");
}

println '---------------------------------';
println map;
println '---------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not copied from Jenkins' userContent directory.
	exit /b 1
)

endlocal

@echo off

setlocal

echo [INFO]SEVENZ: %SEVENZ%

if EXIST %AMDAPPSDKROOT% (
	echo [INFO]Directory %AMDAPPSDKROOT% exists, deleting it.
	rmdir /S /Q "%AMDAPPSDKROOT%"
)

::- Extract the include files
for /r %%i in (OpenCL_*_%OSTYPE%_release_%PLATFORM_ARCH_AMD%.7z) do (
	%SEVENZ% e -aoa "%%i" -o%AMDAPPSDKROOT%\include\CL -i!release\include\opencl2.0\CL\*.h -i!release\include\opencl2.0\CL\*.hpp
	if ERRORLEVEL 1 (
		echo [ERR]Could not extract the include files from OpenCL_*_%OSTYPE%_release_%PLATFORM_ARCH_AMD%.7z
		exit /b ERRORLEVEL
	)

	::- Extract the libs needed for compilation
	%SEVENZ% e -aoa "%%i" -o%AMDAPPSDKROOT%\lib\%PLATFORM_ARCH_INTEL_ALT% -i!release\lib\%PLATFORM_ARCH_INTEL_ALT%\*.lib
	if ERRORLEVEL 1 (
		echo [ERR]Could not extract the lib files from OpenCL_*_%OSTYPE%_release_%PLATFORM_ARCH_AMD%.7z
		exit /b ERRORLEVEL
	)
)

echo [SUCCESS]Done extracting the AMDAPPSDKROOT

endlocal

@echo off
setlocal
echo AMDAPPSDKROOT: %AMDAPPSDKROOT% echo MSVC.DIR: %MSVC.DIR% echo JAVA_HOME: %JAVA_HOME%
echo [SUCCESS]Copying APARAPI\updated\build.* to com.amd.aparapi.jni copy /V /Y APARAPI\updated\build.* com.amd.aparapi.jni if ERRORLEVEL 1 ( exit /b ERRORLEVEL )
echo [SUCCESS]Done copying APARAPI\updated\build.* to com.amd.aparapi.jni
endlocal
