import java.lang.String;

//Dump the environment variables
def map = [:];
map.put( "ARCHIVE_EXTENSION", "tar.bz2" );

def parAPPSDK_DIR_VERSION;
try {
	parAPPSDK_DIR_VERSION = APPSDK_DIR_VERSION;
	println 'APPSDK_DIR_VERSION found: ' + APPSDK_DIR_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_DIR_VERSION not present
	println 'APPSDK_DIR_VERSION NOT found';
	parAPPSDK_DIR_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parAPPSDK_DIR_VERSION += "-" + MILESTONE;
	}
	map.put( "APPSDK_DIR_VERSION", parAPPSDK_DIR_VERSION );
}

def parAPPSDK_METRIC_VERSION;
try {
	parAPPSDK_METRIC_VERSION = APPSDK_METRIC_VERSION + "L";
	println 'APPSDK_METRIC_VERSION found: ' + APPSDK_METRIC_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_METRIC_VERSION not present
	println 'APPSDK_METRIC_VERSION NOT found';
	parAPPSDK_METRIC_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parAPPSDK_METRIC_VERSION += "-" + MILESTONE;
	}

	parAPPSDK_METRIC_VERSION += "L";

	map.put( "APPSDK_METRIC_VERSION", parAPPSDK_METRIC_VERSION );
}

def parPRODUCTVERSION;
try {
	parPRODUCTVERSION = PRODUCTVERSION;
	println 'PRODUCTVERSION found: ' + PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//PRODUCTVERSION not present
	println 'PRODUCTVERSION NOT found';
	parPRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER 
	if ( "0" != SUBMINOR_NUMBER ) {
		parPRODUCTVERSION+="-" + SUBMINOR_NUMBER;
	}

	parPRODUCTVERSION+="." + BUILD_NUMBER;
	map.put( "PRODUCTVERSION", parPRODUCTVERSION );
}

def parVERSION;
try {
	parVERSION = VERSION;
	println 'VERSION found: ' + VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VERSION not present
	println 'VERSION NOT found';
	parVERSION=parPRODUCTVERSION + ".0";
	map.put( "VERSION", parVERSION );	
}

def parARTIFACT_PRODUCTVERSION;
try {
	parARTIFACT_PRODUCTVERSION = ARTIFACT_PRODUCTVERSION;
	println 'ARTIFACT_PRODUCTVERSION found: ' + ARTIFACT_PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'ARTIFACT_PRODUCTVERSION NOT found';
	parARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + "." + BUILD_NUMBER + "-" + MILESTONE;
	map.put( "ARTIFACT_PRODUCTVERSION", parARTIFACT_PRODUCTVERSION );	
}

def PLATFORM_ARCH_SHORT="";
if( JOB_NAME.contains( "x86" ) ) {
	PLATFORM_ARCH_SHORT="x86"
	map.put( "PLATFORM_ARCH_SHORT", PLATFORM_ARCH_SHORT );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "PLATFORM_ARCH_ALT", "x86" );
	map.put( "ARCH", "32" );
}

if( JOB_NAME.contains( "amd64" ) ) {
	PLATFORM_ARCH_SHORT="x64"
	map.put( "PLATFORM_ARCH_SHORT", PLATFORM_ARCH_SHORT );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "PLATFORM_ARCH_ALT", "x86_64" );
	map.put( "ARCH", "64" );
}

String FILES_TO_COPY="verpatch/verpatch.exe";

map.put( "EXCLUDE_BOLT", "" );
map.put( "EXCLUDE_OPENCL", "" );
map.put( "EXCLUDE_OPENCV", "" );

// Now we check the JOB_NAME if it is exactly equal to APPSDK_BUILD_L then we do not copy any other stuff, otherwise we do based on 
// The job
if ( "APPSDK_BUILD_L" == JOB_NAME ) {
	//do nothing
	println 'Parent Job. Nothing to do.';
}
else if ( JOB_NAME.contains( "OpenCV" ) ) {
	//Need to copy OpenNI
	FILES_TO_COPY += ", OpenNI/" + OPENNI_VERSION + "/" + OSTYPE + "/OpenNI-Linux-" + PLATFORM_ARCH_SHORT + "-" + OPENNI_VERSION + ".tar.bz2";
	map[ "EXCLUDE_BOLT" ] = "-";
	map[ "EXCLUDE_OPENCL" ] = "-";
}
else if ( JOB_NAME.contains( "Bolt," ) ) {
	//Need to copy Bolt libraries Bolt-1.2-Linux-GCC-4.7.3.tar.gz
	FILES_TO_COPY += ", Bolt/" + BOLTLIB_VERSION + "/" + OSTYPE + "/Bolt-" + BOLTLIB_VERSION + "-Linux-GCC-4.7.3.tar.gz";

	//tbb42_20140601oss_lin.tgz
	FILES_TO_COPY += ", TBB/" + TBB_VERSION + "/" + OSTYPE + "/tbb" + TBB_VERSION + "oss_lin.tgz";

	if( JOB_NAME.contains( "x86" ) ) {
		map.put( "BOLT_LIBDIR_NAME", "lib" );
	}
	else if( JOB_NAME.contains( "amd64" ) ) {
		map.put( "BOLT_LIBDIR_NAME", "lib64" );
	}

	map[ "EXCLUDE_OPENCV" ] = "-";
	map[ "EXCLUDE_OPENCL" ] = "-";
}
else if ( JOB_NAME.contains( "OpenCL" ) ) {
	map[ "EXCLUDE_OPENCV" ] = "-";
	map[ "EXCLUDE_BOLT" ] = "-";
}
else {
	println 'Should not have come here' ;
}

map.put( "FILES_TO_COPY", FILES_TO_COPY );

println '-------------------------------------'
println 'Evnironment Variables Added: ' + map;
println 'JOB_NAME: ' + JOB_NAME;
println '-------------------------------------'

return map;

hudson.tasks.Shell.command:
#!/bin/bash #This script removes samples which need not be built from CmakeLists.txt
if [ $S = "OpenCL" ]; then if [ $P = "x86" ]; then #Remove the SVMAtomicsBinaryTreeInsert from the CMakeLists.txt present in #//workspace/samples/opencl/cl/CMakeLists.txt sed -i.bak '/SVMAtomicsBinaryTreeInsert/d' "$WORKSPACE/samples/${S,,}/cl/CMakeLists.txt" if [ $? -ne 0 ]; then echo FAILURE: Could not remove the SVMAtomicsBinaryTreeInsert from $WORKSPACE/samples/${S,,}/cl/CMakeLists.txt else rm -vf "$WORKSPACE/samples/${S,,}/cl/CMakeLists.txt.bak" fi fi fi
if [ $S = "Bolt" ]; then if [ $P = "x86" ]; then #Remove the StocksDataAnalysis from the CMakeLists.txt present in #//workspace/samples/${S,,}/CMAakeLists.txt sed -i.bak '/StocksDataAnalysis/d' "$WORKSPACE/samples/${S,,}/CMakeLists.txt" if [ $? -ne 0 ]; then echo FAILURE: Could not remove the StocksDataAnalysis from $WORKSPACE/samples/${S,,}/CMakeLists.txt else rm -vf "$WORKSPACE/samples/${S,,}/CMakeLists.txt.bak" fi fi fi
