import java.lang.String;

//Dump the environment variables
def map = [:];

def parAPPSDK_DIR_VERSION;
try {
	parAPPSDK_DIR_VERSION = APPSDK_DIR_VERSION;
	println 'APPSDK_DIR_VERSION found: ' + APPSDK_DIR_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_DIR_VERSION not present
	println 'APPSDK_DIR_VERSION NOT found';
	parAPPSDK_DIR_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parAPPSDK_DIR_VERSION += "-" + MILESTONE;
	}
	map.put( "APPSDK_DIR_VERSION", parAPPSDK_DIR_VERSION );
}

def parAPPSDK_METRIC_VERSION;
try {
	parAPPSDK_METRIC_VERSION = APPSDK_METRIC_VERSION;
	println 'APPSDK_METRIC_VERSION found: ' + APPSDK_METRIC_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_METRIC_VERSION not present
	println 'APPSDK_METRIC_VERSION NOT found';
	parAPPSDK_METRIC_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parAPPSDK_METRIC_VERSION += "-" + MILESTONE;
	}
	map.put( "APPSDK_METRIC_VERSION", parAPPSDK_METRIC_VERSION );
}

def parPRODUCTVERSION;
try {
	parPRODUCTVERSION = PRODUCTVERSION;
	println 'PRODUCTVERSION found: ' + PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//PRODUCTVERSION not present
	println 'PRODUCTVERSION NOT found';
	parPRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER 
	if ( "0" != SUBMINOR_NUMBER ) {
		parPRODUCTVERSION+="-" + SUBMINOR_NUMBER;
	}

	parPRODUCTVERSION+="." + BUILD_NUMBER;
	map.put( "PRODUCTVERSION", parPRODUCTVERSION );
}

def parVERSION;
try {
	parVERSION = VERSION;
	println 'VERSION found: ' + VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VERSION not present
	println 'VERSION NOT found';
	parVERSION=parPRODUCTVERSION + ".0";
	map.put( "VERSION", parVERSION );	
}

def parARTIFACT_PRODUCTVERSION;
try {
	parARTIFACT_PRODUCTVERSION = ARTIFACT_PRODUCTVERSION;
	println 'ARTIFACT_PRODUCTVERSION found: ' + ARTIFACT_PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'ARTIFACT_PRODUCTVERSION NOT found';
	parARTIFACT_PRODUCTVERSION = MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + "." + BUILD_NUMBER + "-" + MILESTONE;
	map.put( "ARTIFACT_PRODUCTVERSION", parARTIFACT_PRODUCTVERSION );	
}

map.put( "ARCHIVE_EXTENSION", "7z" );

if( JOB_NAME.contains( "Win32" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x86" );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
}

if( JOB_NAME.contains( "x64" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x64" );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86_64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
}

String strVSName = "";
if ( JOB_NAME.contains( "v120" ) ) {
	strVSName = "VS2013";
	map.put( "VCTOOLKIT_SHORT", "vc12" );
	map.put( "VSKEY", "12.0" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	strVSName = "VS2012";
	map.put( "VCTOOLKIT_SHORT", "vc11" );
	map.put( "VSKEY", "11.0" );
}

if ( JOB_NAME.contains( "v100" ) ) {
	strVSName = "VS2010";
	map.put( "VCTOOLKIT_SHORT", "vc10" );
	map.put( "VSKEY", "10.0" );
}

map.put( "VSNAME", strVSName );

String FILES_TO_COPY="verpatch/verpatch.exe";

// Now we check the JOB_NAME if it is exactly equal to APPSDK_BUILD_W then we do not copy any other stuff, otherwise we do based on 
// The job
if ( "APPSDK_BUILD_W" == JOB_NAME ) {
	//do nothing
	println 'Parent Job. Nothing to do.';
}
else if ( JOB_NAME.contains( "OpenCL" ) ) {
	FILES_TO_COPY += ", OpenCL/2.0/" + OSTYPE + "/*.zip";
}
else if ( JOB_NAME.contains( "OpenCV" ) ) {
	//Need to copy OpenNI
	if( JOB_NAME.contains( "Win32" ) ) {
		FILES_TO_COPY += ", OpenNI/" + OPENNI_VERSION + "/windows/OpenNI-Windows-x86-" + OPENNI_VERSION + ".zip";
	}
	else if ( JOB_NAME.contains( "x64" ) ) {
		FILES_TO_COPY += ", OpenNI/" + OPENNI_VERSION + "/windows/OpenNI-Windows-x64-" + OPENNI_VERSION + ".zip";
	}
	else {
		//nothing to do, should not have come here
		println 'Control should not come here';
	}
}
else if ( JOB_NAME.contains( "Bolt," ) ) {
	//Need to copy Bolt libraries
	FILES_TO_COPY += ", Bolt/" + BOLTLIB_VERSION + "/windows/Bolt-" + BOLTLIB_VERSION + "-" + strVSName + ".zip";
	FILES_TO_COPY += ", TBB/" + TBB_VERSION + "/" + OSTYPE + "/tbb" + TBB_VERSION + "oss_win.zip"
	if( JOB_NAME.contains( "Win32" ) ) {
		map.put( "BOLT_LIBDIR_NAME", "lib" );
	}
	else if( JOB_NAME.contains( "x64" ) ) {
		map.put( "BOLT_LIBDIR_NAME", "lib64" );
	}
}

map.put( "FILES_TO_COPY", FILES_TO_COPY );
map.put( "J", "1.8" );

if( JOB_NAME.contains( "Release" ) ) {
	map.put( "C_SHORT", "rel" );
	map.put( "C_SC", "release" );
}

if( JOB_NAME.contains( "Debug" ) ) {
	map.put( "C_SHORT", "dbg" );
	map.put( "C_SC", "debug" );
}

println '-------------------------------------'
println 'Evnironment Variables Added: ' + map;
println 'JOB_NAME: ' + JOB_NAME;
println '-------------------------------------'

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not copied from Jenkins' userContent directory.
	exit /b 1
)

endlocal

@echo off
REM Copyright ©2015 Advanced Micro Devices, Inc. All rights reserved.

setlocal

:: then extract the libs and binaries in the appropriate directories

:: First extract the headers in the include directory
:: create the directory if not exists
set OUTPUT_DIR=%WORKSPACE%\include\CL
if NOT EXIST "%OUTPUT_DIR%" (
	mkdir "%OUTPUT_DIR%"
)

%SEVENZ% e -aoa "%WORKSPACE%\OpenCL_*_windows_%C_SC%_x86.7z" -i!%C_SC%\include\opencl2.0\CL\* -o"%OUTPUT_DIR%"
if ERRORLEVEL 1 ( 
	echo [ERR]Failed to extract the contents of %C_SC%\include\Opencl2.0 to %OUTPUT_DIR% from the archive: OpenCL_*_windows_%C_SC%_x86.7z
	exit /b ERRORLEVEL
)

::OpenCL 2.0 headers extracted successfully.

::Now extract the 32-bit libraries
set OUTPUT_DIR=%WORKSPACE%\lib\win32
if NOT EXIST "%OUTPUT_DIR%" (
	mkdir "%OUTPUT_DIR%"
)
%SEVENZ% e -aoa "%WORKSPACE%\OpenCL_*_windows_%C_SC%_x86.7z" -i!%C_SC%\lib\x86\* -x!%C_SC%\lib\x86\*.bc -x!%C_SC%\lib\x86\libhsail.lib -o"%OUTPUT_DIR%"
if ERRORLEVEL 1 ( 
	echo [ERR]Failed to extract the contents of %C_SC%\lib\x86 to %OUTPUT_DIR% from the archive: OpenCL_*_windows_%C_SC%_x86.7z
	exit /b ERRORLEVEL
)

::Now extract the 64-bit libraries
set OUTPUT_DIR=%WORKSPACE%\lib\win64
if NOT EXIST "%OUTPUT_DIR%" (
	mkdir "%OUTPUT_DIR%"
)
%SEVENZ% e -aoa "%WORKSPACE%\OpenCL_*_windows_%C_SC%_amd64.7z" -i!%C_SC%\lib\x86_64\* -x!%C_SC%\lib\x86_64\*.bc -x!%C_SC%\lib\x86_64\libhsail.lib -o"%OUTPUT_DIR%"
if ERRORLEVEL 1 ( 
	echo [ERR]Failed to extract the contents of %C_SC%\lib\x86_64 to %OUTPUT_DIR% from the archive: OpenCL_*_windows_%C_SC%_amd64.7z
	exit /b ERRORLEVEL
)

::Now extract the 32-bit binaries
set OUTPUT_DIR=%WORKSPACE%\bin\x86
if NOT EXIST "%OUTPUT_DIR%" (
	mkdir "%OUTPUT_DIR%"
)
%SEVENZ% e -aoa "%WORKSPACE%\OpenCL_*_windows_%C_SC%_x86.7z" -i!%C_SC%\bin\x86\amdhsasc.dll -i!%C_SC%\bin\x86\amdocl*.* -i!%C_SC%\bin\x86\clinfo.exe -i!%C_SC%\bin\x86\Open*.dll -i!%C_SC%\bin\x86\OVDecode.dll -o"%OUTPUT_DIR%"
if ERRORLEVEL 1 ( 
	echo [ERR]Failed to extract the contents of %C_SC%\lib\x86 to %OUTPUT_DIR% from the archive: OpenCL_*_windows_%C_SC%_x86.7z
	exit /b ERRORLEVEL
)

::Now extract the 64-bit binaries
set OUTPUT_DIR=%WORKSPACE%\bin\x86_64
if NOT EXIST "%OUTPUT_DIR%" (
	mkdir "%OUTPUT_DIR%"
)
%SEVENZ% e -aoa "%WORKSPACE%\OpenCL_*_windows_%C_SC%_amd64.7z" -i!%C_SC%\bin\x86_64\amdhsasc64.dll -i!%C_SC%\bin\x86_64\amdocl*.* -i!%C_SC%\bin\x86_64\clinfo.exe -i!%C_SC%\bin\x86_64\Open*.dll -i!%C_SC%\bin\x86_64\OVDecode.dll -o"%OUTPUT_DIR%"
if ERRORLEVEL 1 ( 
	echo [ERR]Failed to extract the contents of %C_SC%\lib\x86 to %OUTPUT_DIR% from the archive: OpenCL_*_windows_%C_SC%_x86.7z
	exit /b ERRORLEVEL
)
endlocal

@echo off
REM Copyright ©2014 Advanced Micro Devices, Inc. All rights reserved.

setlocal

echo [INFO]Calling %SEVENZ% x -aoa "L=LBM,P=%PLATFORM_ARCH_AMD%\opencv-*-%OSTYPE%.%ARCHIVE_EXTENSION%" -o"%WORKSPACE%"
%SEVENZ% x -aoa "L=LBM,P=%PLATFORM_ARCH_AMD%\opencv-*-%OSTYPE%.%ARCHIVE_EXTENSION%" -o"%WORKSPACE%"
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file opencv-*-%OSTYPE%.%ARCHIVE_EXTENSION% to %WORKSPACE%
	exit /b ERRORLEVEL
)

echo [INFO]Calling %SEVENZ% x -aoa opencv-*.exe -ir!opencv\build\include
%SEVENZ% x -aoa opencv-*.exe -ir!opencv\build\include
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file opencv-*.exe
	exit /b ERRORLEVEL
)

echo [INFO]Calling %SEVENZ% x -aoa OpenNI\%OPENNI_VERSION%\%OSTYPE%\OpenNI-Windows-*-%OPENNI_VERSION%.zip -o"%WORKSPACE%"
%SEVENZ% x -aoa OpenNI\%OPENNI_VERSION%\%OSTYPE%\OpenNI-Windows-*-%OPENNI_VERSION%.zip -o"%WORKSPACE%"
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file OpenNI\%OPENNI_VERSION%\%OSTYPE%\OpenNI-Windows-*-%OPENNI_VERSION%.zip to %WORKSPACE%
	exit /b ERRORLEVEL
)

::-Since at a time only one instance of MSIEXEC can run on a system, hence we check and if found running we wait for the other msiexec to complete its execution
:LOOP
tasklist /fi "imagename eq msiexec.exe" |find ":" > nul
if ERRORLEVEL 1 (
	echo [INFO]msiexec.exe is running, waiting for 3 seconds to re-check.
	ping 1.1.1.1 -n 1 -w 3000 > nul
	goto LOOP
) else (
	echo [INFO]msiexec.exe not running, continuing with the rest of the processing.
)

echo [INFO]Calling %MSIEXEC% /a OpenNI-Windows-%PLATFORM_ARCH_SHORT%-%OPENNI_VERSION%.msi /qn TARGETDIR=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%
%MSIEXEC% /a OpenNI-Windows-%PLATFORM_ARCH_SHORT%-%OPENNI_VERSION%.msi /qn TARGETDIR=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%
if ERRORLEVEL 1 (
	echo [ERR]Could not do administrative install of OpenNI-Windows-%PLATFORM_ARCH_SHORT%-%OPENNI_VERSION%.msi in the directory %WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%
	exit /b ERRORLEVEL
)

echo [INFO]Calling %SEVENZ% x -aoa opencv-*.exe -ir!opencv\build\%PLATFORM_ARCH_SHORT%\%VCTOOLKIT_SHORT%\lib
%SEVENZ% x -aoa opencv-*.exe -ir!opencv\build\%PLATFORM_ARCH_SHORT%\%VCTOOLKIT_SHORT%\lib
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file opencv-*.exe
	exit /b ERRORLEVEL
)

endlocal

@echo off
REM Copyright ©2014 Advanced Micro Devices, Inc. All rights reserved.

setlocal

%SEVENZ% x -aoa Bolt\%BOLTLIB_VERSION%\%OSTYPE%\Bolt-%BOLTLIB_VERSION%*-%VSNAME%*.zip -ir!Bolt-%BOLTLIB_VERSION%*-%VSNAME%\include\* -ir!Bolt-%BOLTLIB_VERSION%*-%VSNAME%\%BOLT_LIBDIR_NAME%\*
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file Bolt\%BOLTLIB_VERSION%\%OSTYPE%\Bolt-%BOLTLIB_VERSION%*-%VSNAME%*.zip to %WORKSPACE%
	exit /b ERRORLEVEL
)

%SEVENZ% x -aoa TBB\%TBB_VERSION%\%OSTYPE%\tbb%TBB_VERSION%oss_win.zip -ir!tbb%TBB_VERSION%oss\include\* -ir!tbb%TBB_VERSION%oss\lib\%PLATFORM_ARCH_INTEL%\%VCTOOLKIT_SHORT%\*
if ERRORLEVEL 1 (
	echo [ERR]Could not extract file TBB\%TBB_VERSION%\%OSTYPE%\tbb%TBB_VERSION%oss_win.zip to %WORKSPACE%
	exit /b ERRORLEVEL
)

endlocal

@echo off
REM Copyright ©2014 Advanced Micro Devices, Inc. All rights reserved.

echo Configuration: %C%
echo Platform: %P%
echo PlatformToolset: %T%
echo Sample: %S%
echo PRODUCTVERSION: %PRODUCTVERSION%
echo VERSION: %VERSION%
echo ARTIFACT_PRODUCTVERSION: %ARTIFACT_PRODUCTVERSION%
echo ARCHIVE_EXTENSION: %ARCHIVE_EXTENSION%
echo VCTOOLKIT_SHORT: %VCTOOLKIT_SHORT%
echo PLATFORM_ARCH_SHORT: %PLATFORM_ARCH_SHORT%
echo MappingFile: %MappingFile%

setlocal enabledelayedexpansion

set S_LC=%S%
set S_SLN_NAME=%S%
set VS=

call samples\P4Update.bat %MappingFile%

if "%T%"=="v100" (
	set VS=VS10
)

if "%T%"=="v110" (
	set VS=VS12
)

if "%T%"=="v120" (
	set VS=VS13
)

if "%S%"=="C++Amp" (
	set S_LC=%S%
) 

if "%S%"=="Bolt_TBB" (
	set S_LC=Bolt
	set S_SLN_NAME=Bolt
) else (
	set S_LC
	CALL :LoCase S_LC
)

echo Sample LC: %S_LC%

REM Update the version information in the source files mentioned in the MasterSampleVersion.csv
call samples\UpdateVersionInSource.bat %WORKSPACE%\samples\MasterSampleVersion.csv %PRODUCTVERSION%

REM Perform Sample specific customizations.
REM For Example Bolt requires certain archives to be extracted and environment variables set 
REM Same is the case with OpenCV

if "%S%"=="OpenCV" (
	echo ------------Performing customizations for OpenCV-----------------
	
	REM Do OpenCV related customizations
	REM For building OpenCV samples, the following are needed:
	if "%P%"=="Win32" (
		set OPENNI2_LIB=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%\OpenNI2\Lib
		set OPENNI2_INCLUDE=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%\OpenNI2\Include
		
		if Not exist "lib\x86" (
			md "lib\x86"
			if Not ERRORLEVEL 0 (
				echo FAILURE: Could not create folder %WORKSPACE%\%AMDAPPSDKROOT%\lib\x86
				exit /B ERRORLEVEL
			)
		)
		
		xcopy lib\win32\* lib\x86 /i/s
		if Not ERRORLEVEL 0 (
			echo FAILURE: Could not copy the contents of lib\win32 to lib\x86
			exit /b ERRORLEVEL
		)
	)
	
	if "%P%"=="x64" (
		set OPENNI2_LIB64=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%\OpenNI2\Lib
		set OPENNI2_INCLUDE64=%WORKSPACE%\OpenNI2\%PLATFORM_ARCH_SHORT%\OpenNI2\Include
		
		if Not exist "lib\x86_64" (
			md "lib\x86_64"
			if Not ERRORLEVEL 0 (
				echo FAILURE: Could not create folder %WORKSPACE%\%AMDAPPSDKROOT%\lib\x86_64
				exit /B ERRORLEVEL
			)
		)
		
		xcopy lib\win64\* lib\x86_64 /i/s
		if Not ERRORLEVEL 0 (
			echo FAILURE: Could not copy the contents of lib\win32 to lib\x86_64
			exit /b ERRORLEVEL
		)
	)
	
	echo OPENNI2_LIB: !OPENNI2_LIB!
	echo OPENNI2_INCLUDE: !OPENNI2_INCLUDE!
	echo OPENNI2_LIB64: !OPENNI2_LIB64!
	echo OPENNI2_INCLUDE64: !OPENNI2_INCLUDE64!
	
	::-Get the OPENCV_BUILD_NUMBER
	::-------------------------------------------
	for %%i in (opencv-*.exe) do (
		set OPENCV_FILENAME=%%~ni
	)

	echo OpenCV filename: !OPENCV_FILENAME!

	for /f "tokens=2 delims=-" %%a in ('echo !OPENCV_FILENAME!') do (
		set OPENCV_VERSION_NUMBER=%%a
	)

	echo OPENCV_VERSION_NUMBER: !OPENCV_VERSION_NUMBER!

	for /f "tokens=1,2,3 delims=." %%a in ('echo !OPENCV_FILENAME!') do (
		set VERSION_NUMBER=%%a
		set OPENCV_MINOR_NUMBER=%%b
		set OPENCV_SUBMINOR_NUMBER=%%c
	)

	for /f "tokens=2 delims=-" %%a in ('echo !VERSION_NUMBER!') do (
		set OPENCV_MAJOR_NUMBER=%%a
	)

	echo OPENCV_MAJOR_NUMBER: !OPENCV_MAJOR_NUMBER!
	echo OPENCV_MINOR_NUMBER: !OPENCV_MINOR_NUMBER!
	echo OPENCV_SUBMINOR_NUMBER: !OPENCV_SUBMINOR_NUMBER!

	set OCVCL_VER=!OPENCV_MAJOR_NUMBER!!OPENCV_MINOR_NUMBER!!OPENCV_SUBMINOR_NUMBER!

	echo ------------Done performing customizations for OpenCV-----------------
)

REM  Build the Sample using MSBUILD.

echo OPENNI2_LIB: %OPENNI2_LIB%
echo OPENNI2_INCLUDE: %OPENNI2_INCLUDE%
echo OPENNI2_LIB64: %OPENNI2_LIB64%
echo OPENNI2_INCLUDE64: %OPENNI2_INCLUDE64%
echo OCVCL_VER: %OCVCL_VER%

echo Calling: !MSBUILD! /m /t:build /p:Configuration=%C% /p:Platform=%P% /p:PlatformToolset=%T% samples\%S_LC%\%S_SLN_NAME%Samples%VS%.sln
!MSBUILD! /m /t:build /p:Configuration=%C% /p:Platform=%P% /p:PlatformToolset=%T% samples\%S_LC%\%S_SLN_NAME%Samples%VS%.sln
set Retval=%ERRORLEVEL%

REM Ignore the return value in case of Bolt Release Win32 and Bolt Debug
REM As BoltAMPIntro sample crashes cl.exe of Visual Studio 2012
if "%S%"=="Bolt" (
	if "%T%"=="v110" (
		if "%C%"=="Debug" (
			set Retval=0
		)
		
		if "%C%"=="Release" (
			if "%P%"=="Win32" (
				set Retval=0
			)
		)
	)
)

echo Retval: %Retval%

if "%S%"=="Bolt" (
	REM Build Bolt_TBB Configurations
	if %Retval% EQU 0 (
		echo Calling: !MSBUILD! /m /t:build /p:Configuration=%C%_TBB /p:Platform=%P% /p:PlatformToolset=%T% samples\%S_LC%\%S_SLN_NAME%Samples%VS%.sln
		!MSBUILD! /m /t:build /p:Configuration=%C%_TBB /p:Platform=%P% /p:PlatformToolset=%T% samples\%S_LC%\%S_SLN_NAME%Samples%VS%.sln
		set Retval=%ERRORLEVEL%
		if "%T%"=="v110" (
			if "%C%"=="Debug" (
				set Retval=0
			)
			
			if "%C%"=="Release" (
				if "%P%"=="Win32" (
					set Retval=0
				)
			)
		)
	)
)

if %Retval% EQU 0 (
	REM Add Version Property to the built binaries (.exe and .dll files only)
	call %WORKSPACE%\samples\Version.bat %S_LC% %VERPATCH% %PRODUCTVERSION%
	
	REM  Archive the built binaries
	%SEVENZ% a -t7z APPSDK_bin_%S%_%C%_%P%_%T%_%ARTIFACT_PRODUCTVERSION%.%ARCHIVE_EXTENSION% -r samples\%S_LC%\bin -xr^^!*.ilk -xr^^!*.pdb -xr^^!*.exp -xr^^!*.log -xr^^!*.log -xr^^!*.lib
	if ERRORLEVEL 0 (
		REM  Archive the sample sources for the samples
		REM  The sample sources shall be built only when PlatformToolset is v110 and Platform is x64.
		REM  The PlatformToolset is variable based so that it can be configured.
		
		if "%T%"=="%SOURCETOOLKIT%" (
			if "%P%"=="x64" (
				REM  Archive the Sample Sources
				echo Archiving %S% sample sources and sample headers
				%SEVENZ% a -t7z APPSDK_sources_%S%_%ARTIFACT_PRODUCTVERSION%.%ARCHIVE_EXTENSION% -r samples\%S_LC% -ir^^!include\SDKUtil -xr^^!Debug -xr^^!Release -xr^^!bin -xr^^!ipch -xr^^!temp -x^^!*.sdf
 
				if %ERRORLEVEL% EQU 0 (
				
					REM Now we create Sample Specific archives
					REM For OpenCL
					if "%S%"=="OpenCL" (
						echo Copying include and LIBs to AMDAPPSDKROOT
						if Not exist "AMDAPPSDKROOT\include\CL" (
							md "AMDAPPSDKROOT\include\CL"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\include\CL
								exit /B ERRORLEVEL
							)
						)
						
						xcopy include\CL\* AMDAPPSDKROOT\include\CL /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of include\CL to AMDAPPSDKROOT\include\CL
							exit /b ERRORLEVEL
							
						)
						
						if Not exist "AMDAPPSDKROOT\include\GL" (
							md "AMDAPPSDKROOT\include\GL"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\include\GL
								exit /B ERRORLEVEL
							)
						)
						
						xcopy include\GL\* AMDAPPSDKROOT\include\GL /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of include\GL to AMDAPPSDKROOT\include\GL
							exit /b ERRORLEVEL
							
						)

						if Not exist "AMDAPPSDKROOT\lib\x86" (
							md "AMDAPPSDKROOT\lib\x86"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\lib\x86
								exit /B ERRORLEVEL
							)
						)

						xcopy lib\win32\* AMDAPPSDKROOT\lib\x86 /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of lib\win32 to AMDAPPSDKROOT\lib\x86
							exit /b ERRORLEVEL
						)
						
						if Not exist "AMDAPPSDKROOT\lib\x86_64" (
							md "AMDAPPSDKROOT\lib\x86_64"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\lib\x86_64
								exit /B ERRORLEVEL
							)
						)
						
						xcopy lib\win64\* AMDAPPSDKROOT\lib\x86_64 /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of lib\win64 to AMDAPPSDKROOT\lib\x86_64
							exit /b ERRORLEVEL
						)
						
						if Not exist "AMDAPPSDKROOT\bin\x86" (
							md "AMDAPPSDKROOT\bin\x86"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\bin\x86
								exit /B ERRORLEVEL
							)
						)

						xcopy bin\x86\* AMDAPPSDKROOT\bin\x86 /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of bin\win32 to AMDAPPSDKROOT\bin\x86
							exit /b ERRORLEVEL
						)
						
						if Not exist "AMDAPPSDKROOT\bin\x86_64" (
							md "AMDAPPSDKROOT\bin\x86_64"
							if Not ERRORLEVEL 0 (
								echo FAILURE: Could not create folder %WORKSPACE%\AMDAPPSDKROOT\bin\x86_64
								exit /B ERRORLEVEL
							)
						)
						
						xcopy bin\x86_64\* AMDAPPSDKROOT\bin\x86_64 /i/s
						if Not ERRORLEVEL 0 (
							echo FAILURE: Could not copy the contents of bin\win64 to AMDAPPSDKROOT\bin\x86_64
							exit /b ERRORLEVEL
						)
						
						pushd AMDAPPSDKROOT\bin
							pushd x86
								copy /V /Y amdocl_as32.exe amdocl_as.exe
								copy /V /Y amdocl_ld32.exe amdocl_ld.exe
							popd
							
							pushd x86_64
								copy /V /Y amdocl_as64.exe amdocl_as.exe
								copy /V /Y amdocl_ld64.exe amdocl_ld.exe
							popd
						popd

						%SEVENZ% a -t7z APPSDK_sources_APPSDKROOT_%ARTIFACT_PRODUCTVERSION%.%ARCHIVE_EXTENSION% -r AMDAPPSDKROOT\
						if Not ERRORLEVEL 0 (
							exit /b ERRORLEVEL
						)
					)
				)
			)
		)
	)
)
set S_LC
endlocal
goto :EOF

