map.put( "PRODUCT_VERSION", PRODUCT_VERSION );
map.put( "ARCHIVE_EXTENSION", "7z" );

def ARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER;
if( "0" != SUBMINOR_NUMBER ) {
	ARTIFACT_PRODUCTVERSION += "-" + SUBMINOR_NUMBER;
}

def PRODUCT_NAME="AMD APP SDK " + ARTIFACT_PRODUCTVERSION;
map.put( "ProductName", PRODUCT_NAME );

//def PRODUCTION_URL_VERSION = ARTIFACT_PRODUCTVERSION + "-" + MILESTONE;
