map.put( "ARTIFACT_PRODUCTVERSION", ARTIFACT_PRODUCTVERSION );
map.put( "PRODUCTION_URL_VERSION", ARTIFACT_PRODUCTVERSION );

def MM_ARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER;
if( "0" != SUBMINOR_NUMBER ) {
	MM_ARTIFACT_PRODUCTVERSION += "-" + SUBMINOR_NUMBER;
}

MM_ARTIFACT_PRODUCTVERSION += "." + APPSDK_BUILD_NUMBER + "." + APPSDK_PKG_MM_NUMBER + "-" + MILESTONE;
map.put( "MM_ARTIFACT_PRODUCTVERSION", MM_ARTIFACT_PRODUCTVERSION );

def parAPPSDK_DIR_VERSION;
try {
	parAPPSDK_DIR_VERSION = APPSDK_DIR_VERSION;
	println 'APPSDK_DIR_VERSION found: ' + APPSDK_DIR_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_DIR_VERSION not present
	println 'APPSDK_DIR_VERSION NOT found';
	parAPPSDK_DIR_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER;
	if( "0" != SUBMINOR_NUMBER ) {
		parAPPSDK_DIR_VERSION += "-" + SUBMINOR_NUMBER;
	}

	if ( "GA" != MILESTONE ) {
		if( JOB_NAME.contains( "IS2012" ) ) {
			parAPPSDK_DIR_VERSION += " " + MILESTONE;
		}
		if (JOB_NAME.contains( "LBM" ) ) {
			parAPPSDK_DIR_VERSION += "-" + MILESTONE;
		}
	}

	map.put( "APPSDK_DIR_VERSION", parAPPSDK_DIR_VERSION );	
}

def parAPPSDK_METRIC_VERSION;
try {
	parAPPSDK_METRIC_VERSION = APPSDK_METRIC_VERSION;
	println 'APPSDK_METRIC_VERSION found: ' + APPSDK_METRIC_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//APPSDK_METRIC_VERSION not present
	println 'APPSDK_METRIC_VERSION NOT found';
	parAPPSDK_METRIC_VERSION = MAJOR_NUMBER + "." + MINOR_NUMBER;
	if( "0" != SUBMINOR_NUMBER ) {
		parAPPSDK_METRIC_VERSION += "-" + SUBMINOR_NUMBER;
	}

	parAPPSDK_METRIC_VERSION += "-" + MILESTONE;

	if( JOB_NAME.contains( "IS2012" ) ) {
		parAPPSDK_METRIC_VERSION += "-W";
	}
	if (JOB_NAME.contains( "LBM" ) ) {
		parAPPSDK_METRIC_VERSION += "-L";
	}

	map.put( "APPSDK_METRIC_VERSION", parAPPSDK_METRIC_VERSION );	
}

map.put( "WINDOWS", "" );
map.put( "LINUX", "" );

if( JOB_NAME == "APPSDK_PKG" ) {
	//parent job
	map.put( "VSNAME", "VS2013" );
	map.put( "VCNAME_FULL", "Visual C++ 2013" );
