if ( JOB_NAME.contains( "LBM" ) ) {
	map.put( "ARCHIVE_EXTENSION", "tar.bz2" );
	map.put( "Bolt", "0" );
	map.put( "OpenCL", "0" );
	map.put( "OpenCV", "0" );
	map.put( "VCNAME_FULL", "Visual C++ 2013" );

	if( SAMPLES.contains( "BOLT" ) ) {
		map[ "Bolt" ] = "1"
