hudson.tasks.BatchFile.command:
@echo off

setlocal

set J=%APARAPI_JDK%

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not found in %WORKSPACE%
	exit /b 1
)

endlocal

@echo off

setlocal

echo APPSDK_PKG_MM: %COPYARTIFACT_BUILD_NUMBER_APPSDK_PKG_MM%
echo SDKInstallManager_BUILD: %COPYARTIFACT_BUILD_NUMBER_SDKInstallManager_BUILD%

echo APPSDK_DIR_VERSION: %APPSDK_DIR_VERSION%
echo APPSDK_METRIC_VERSION: %APPSDK_METRIC_VERSION%

if NOT Exist InstallManagerExtn\Release\Win32 (
	mkdir InstallManagerExtn\Release\Win32
)

%SEVENZ% x -aoa SDKInstallManager_Release_Win32_*_bin.%ARCHIVE_EXTENSION% -i!MSICA.dll -o%WORKSPACE%\InstallManagerExtn\Release\Win32

if ERRORLEVEL 1 (
	exit /b ERRORLEVEL
)

if Not exist MM (
	mkdir MM
)

REM Extract the Sources from the archives (AMD_APP_SDK_sources_MM_APPSDKROOT_3.0-0.112.280-GA_windows.7z)
for /r %%i in (AMD_APP_SDK_sources_MM_*_%MM_ARTIFACT_PRODUCTVERSION%_%OSTYPE%.%ARCHIVE_EXTENSION%) do (
	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
	if ERRORLEVEL 1 (
		exit /b ERRORLEVEL
	)
)
REM Extract the Prebuilt Merge modules from the archives (AMD_APP_SDK_PB_MM_Bolt_Release_Win32_v110_3.0-0.112.280-GA_windows.7z)
for /r %%i in (AMD_APP_SDK_PB_MM_*_%C%_%PLATFORM_ARCH_VS%_%T%_*.%ARCHIVE_EXTENSION%) do (
	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
	if ERRORLEVEL 1 (
		exit /b ERRORLEVEL
	)
)

REM Extract the APPSDK_PB_MM_APARAPI_%T%_JDK%APARAPI_JDK%_*.7z
REM for /r %%i in (APPSDK_PB_MM_APARAPI_%T%_JDK%APARAPI_JDK%_*.%ARCHIVE_EXTENSION%) do (
REM	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
REM	if ERRORLEVEL 1 (
REM		exit /b ERRORLEVEL
REM	)
REM )

endlocal

@echo off
setlocal

echo PRODUCTION_URL_VERSION: %PRODUCTION_URL_VERSION%

set ISM=%WORKSPACE%\APPSDK.ism

:: This script updates the %ISM% with the appropriate URL for the packages to be downloaded.
:: The URL is driven by a build parameter and the default is http://cas.amd.com:8089/APPSDK/%PRODUCT_VERSION%/x86

if defined M (
	if "%M%"=="W" (
		echo Building the Web installers. Updating for developer.amd.com.
		%PERL% -pi.bak -e "s!http://cas.amd.com:8089/APPSDKInstaller/x86!http://amd-dev.wpengine.netdna-cdn.com/app-sdk/installers/APPSDKInstaller/%PRODUCTION_URL_VERSION%/web/x86!g;" "%ISM%"
		%PERL% -pi.bak -e "s!http://cas.amd.com:8089/APPSDKInstaller/amd64!http://amd-dev.wpengine.netdna-cdn.com/app-sdk/installers/APPSDKInstaller/%PRODUCTION_URL_VERSION%/web/x64!g;" "%ISM%"
		
		%PERL% -pi.bak -e "s?Copying new files?Downloading and Installing new files?g;" "%ISM%"
		%PERL% -pi.bak -e "s?Setup is preparing the InstallShield Wizard which will guide you through the program setup process.  Please wait.?Setup is preparing the InstallShield Wizard which will guide you through the program setup process. Please wait while we contact the web for additional information.?g;" "%ISM%"
		%PERL% -pi.bak -e "s?Computing space requirements?Please wait while we contact the web for space requirements?g;" "%ISM%"
		%PERL% -pi.bak -e "s?Resolving source. Please wait...?Resolving source download location. Please wait.?g;" "%ISM%"
	) else (
		echo M is defined but is not W. M=%M%
	)
) else (
	echo M is not defined.
)

endlocal
