import java.lang.String;

println 'JOB_NAME: ' + JOB_NAME;
println 'BUILD_MATRIX: ' + BUILD_MATRIX;

//Dump the environment variables
def map = [:];

def ARCHIVE_EXTENSION = "7z"
map.put( "ARCHIVE_EXTENSION", ARCHIVE_EXTENSION );

def parPRODUCTVERSION;
try {
	parPRODUCTVERSION = PRODUCTVERSION;
	println 'PRODUCTVERSION found: ' + PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//PRODUCTVERSION not present
	println 'PRODUCTVERSION NOT found';
	parPRODUCTVERSION = MAJOR_NUMBER + "." + MINOR_NUMBER + "." + BUILD_NUMBER
	map.put( "PRODUCTVERSION", parPRODUCTVERSION );
}

def parVERSION;
try {
	parVERSION = VERSION;
	println 'VERSION found: ' + VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VERSION not present
	println 'VERSION NOT found';
	parVERSION = parPRODUCTVERSION + ".0";
	map.put( "VERSION", parVERSION );	
}

def parARTIFACT_PRODUCTVERSION;
try {
	parARTIFACT_PRODUCTVERSION = ARTIFACT_PRODUCTVERSION;
	println 'ARTIFACT_PRODUCTVERSION found: ' + ARTIFACT_PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'ARTIFACT_PRODUCTVERSION NOT found';
	parARTIFACT_PRODUCTVERSION = parVERSION;
	map.put( "ARTIFACT_PRODUCTVERSION", parARTIFACT_PRODUCTVERSION );	
}

if( JOB_NAME.contains( "Win32" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x86" );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "ARCH", "32" );
	map.put( "PLATFORM_ARCH_WIN", "Win32" );
}

if( JOB_NAME.contains( "x64" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x64" );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86_64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "ARCH", "64" );
	map.put( "PLATFORM_ARCH_WIN", "Win64" );
}

String strVSName = "";
String strVSNameSmall = "";
String strVSNameCaps = "";

if ( JOB_NAME.contains( "v120" ) ) {
	strVSName = "VS2013";
	strVSNameSmall = "vs13";
	strVSNameCaps = "VS13";
	map.put( "VCTOOLKIT_SHORT", "vc12" );
	map.put( "VSKEY", "12.0" );
	map.put( "BOOST_VS", "msvc-12.0" );
	map.put( "VCNAME_ALT", "VC12" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	strVSName = "VS2012";
	strVSNameSmall = "vs12";
	strVSNameCaps = "VS12";
	map.put( "VCTOOLKIT_SHORT", "vc11" );
	map.put( "VSKEY", "11.0" );
	map.put( "BOOST_VS", "msvc-11.0" );
	map.put( "VCNAME_ALT", "VC11" );
}

if ( JOB_NAME.contains( "v100" ) ) {
	strVSName = "VS2010";
	strVSNameSmall = "vs10";	
	strVSNameCaps = "VS10";
	map.put( "VCTOOLKIT_SHORT", "vc10" );
	map.put( "VSKEY", "10.0" );
	map.put( "BOOST_VS", "msvc-10.0" );
	map.put( "VCNAME_ALT", "VC10" );
}

if ( JOB_NAME.contains( "Debug" ) ) {
	map.put( "C_SC", "debug" );
}
if ( JOB_NAME.contains( "Release" ) ) {
	map.put( "C_SC", "release" );
}

String FILES_TO_COPY="verpatch/verpatch.exe,AMDCert_exp6-2016.pfx";

map.put( "VSNAME_SMALL", strVSNameSmall );
map.put( "VSNAME_CAPS", strVSNameCaps );
map.put( "VSNAME", strVSName );

map.put( "FILES_TO_COPY", FILES_TO_COPY );
map.put( "J", "1.8" );

println '--------------------------------------------------------------';
println 'Environment variables inserted: ' + map;
println '--------------------------------------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off
setlocal enabledelayedexpansion

echo COPYARTIFACT_BUILD_NUMBER_XERCES: %COPYARTIFACT_BUILD_NUMBER_XERCES%

::-Get the Xerces Version number from the xerces artifact file name: xerces-3.1.1-33-inc.7z
::-We are interested in the 3.1.1 in this case
::-------------------------------------------
for /r %%i in (xerces-*-inc.%ARCHIVE_EXTENSION%) do (
	set XERCES_ARTIFACT_FILENAME=%%~ni
)

echo XERCES_ARTIFACT_FILENAME: !XERCES_ARTIFACT_FILENAME!

for /f "tokens=2 delims=-" %%a in ('echo !XERCES_ARTIFACT_FILENAME!') do (
	set XERCES_VERSION=%%a
)

echo XERCES_VERSION: !XERCES_VERSION!
echo XERCES_VERSION=!XERCES_VERSION! > Version.props

endlocal

@echo off

echo [INFO][%~n0%~x0]Calling: %MSBUILD% /m /t:build /p:Configuration=%C% /p:Platform=%P% /p:PlatformToolset=%T% VDK\Common\Common%VSNAME_CAPS%.sln
%MSBUILD% /m /t:build /p:Configuration=%C% /p:Platform=%P% /p:PlatformToolset=%T% VDK\Common\Common%VSNAME_CAPS%.sln
if ERRORLEVEL 1 (
	echo [ERR][%~n0%~x0]Compilation failed.
	exit /b ERRORLEVEL
)

REM Now sign the dll and add Version information to it.
echo [INFO][%~n0%~x0]Calling: AddVersionAndSign.bat %WORKSPACE%\VDK\Common\bin\%P%\%C%\%T% "AMD Utils" *.dll
call AddVersionAndSign.bat %WORKSPACE%\VDK\Common\bin\%P%\%C%\%T% "AMD Utils" *.dll

@echo off
setlocal

REM Create the include file archive. This we do only for C=release, LL=shared, A=64 and T=msvc-12.0
REM Since the headers are all common
pushd VDK\Common
	if "%C%"=="Release" (
		if "%P%"=="x64" (
			if "%T%"=="v120" (
				if "%L%"=="WBM" (
					echo [INFO][%~n0%~x0]Calling: %SEVENZ% a -t7z "%WORKSPACE%\common-src-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!src -ir!build -xr!thirdParty
					%SEVENZ% a -t7z "%WORKSPACE%\common-src-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!src -ir!build -xr!thirdParty
					if ERRORLEVEL 1 (
						echo [ERR][%~n0%~x0]Failed to create archive common-src-%PRODUCTVERSION%.%ARCHIVE_EXTENSION% of the contents of VDK\Common\src and VDK\Common\build directory.
						exit /b ERRORLEVEL
					)
					
					echo [INFO][%~n0%~x0]Calling: %SEVENZ% a -t7z "%WORKSPACE%\common-inc-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!inc -xr!thirdParty
					%SEVENZ% a -t7z "%WORKSPACE%\common-inc-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!inc -xr!thirdParty
					if ERRORLEVEL 1 (
						echo [ERR][%~n0%~x0]Failed to create archive common-inc-%PRODUCTVERSION%.%ARCHIVE_EXTENSION% of the contents of VDK\Common\inc directory.
						exit /b ERRORLEVEL
					)
				)
			)
		)
	)

	echo [INFO][%~n0%~x0]Calling: %SEVENZ% a -t7z "%WORKSPACE%\common-bin-%T%-%C%-%P%-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!bin\*.pdb -ir!bin\*.dll -ir!lib\*.lib -ir!thirdParty
	%SEVENZ% a -t7z "%WORKSPACE%\common-bin-%T%-%C%-%P%-%PRODUCTVERSION%.%ARCHIVE_EXTENSION%" -ir!bin\*.pdb -ir!bin\*.dll -ir!lib\*.lib -ir!thirdParty
	if ERRORLEVEL 1 (
		echo [ERR][%~n0%~x0]Failed to create archive common-bin-%PRODUCTVERSION%.%ARCHIVE_EXTENSION% of the contents of lib bin directories.
	)
popd

endlocal
