import java.lang.String;

//Dump the environment variables
def map = [:];
map.put( "ARCHIVE_EXTENSION", "tar.bz2" );

def parVDK_DIR_VERSION;
try {
	parVDK_DIR_VERSION = VDK_DIR_VERSION;
	println 'VDK_DIR_VERSION found: ' + VDK_DIR_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VDK_DIR_VERSION not present
	println 'VDK_DIR_VERSION NOT found';
	parVDK_DIR_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parVDK_DIR_VERSION += "-" + MILESTONE;
	}
	map.put( "VDK_DIR_VERSION", parVDK_DIR_VERSION );
}

def parVDK_METRIC_VERSION;
try {
	parVDK_METRIC_VERSION = VDK_METRIC_VERSION + "L";
	println 'VDK_METRIC_VERSION found: ' + VDK_METRIC_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VDK_METRIC_VERSION not present
	println 'VDK_METRIC_VERSION NOT found';
	parVDK_METRIC_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "GA" != MILESTONE ) {
		parVDK_METRIC_VERSION += "-" + MILESTONE;
	}

	parVDK_METRIC_VERSION += "L";

	map.put( "VDK_METRIC_VERSION", parVDK_METRIC_VERSION );
}

def parPRODUCTVERSION;
try {
	parPRODUCTVERSION = PRODUCTVERSION;
	println 'PRODUCTVERSION found: ' + PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//PRODUCTVERSION not present
	println 'PRODUCTVERSION NOT found';
	parPRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER 
	if ( "0" != SUBMINOR_NUMBER ) {
		parPRODUCTVERSION+="-" + SUBMINOR_NUMBER;
	}

	parPRODUCTVERSION+="." + BUILD_NUMBER;
	map.put( "PRODUCTVERSION", parPRODUCTVERSION );
}

def parVERSION;
try {
	parVERSION = VERSION;
	println 'VERSION found: ' + VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//VERSION not present
	println 'VERSION NOT found';
	parVERSION=parPRODUCTVERSION + ".0";
	map.put( "VERSION", parVERSION );	
}

def parARTIFACT_PRODUCTVERSION;
try {
	parARTIFACT_PRODUCTVERSION = ARTIFACT_PRODUCTVERSION;
	println 'ARTIFACT_PRODUCTVERSION found: ' + ARTIFACT_PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'ARTIFACT_PRODUCTVERSION NOT found';
	parARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + "." + BUILD_NUMBER + "-" + MILESTONE;
	map.put( "ARTIFACT_PRODUCTVERSION", parARTIFACT_PRODUCTVERSION );	
}

def PLATFORM_ARCH_SHORT="";
if( JOB_NAME.contains( "x86" ) ) {
	PLATFORM_ARCH_SHORT="x86"
	map.put( "PLATFORM_ARCH_SHORT", PLATFORM_ARCH_SHORT );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "PLATFORM_ARCH_ALT", "x86" );
	map.put( "ARCH", "32" );
}

if( JOB_NAME.contains( "amd64" ) ) {
	PLATFORM_ARCH_SHORT="x64"
	map.put( "PLATFORM_ARCH_SHORT", PLATFORM_ARCH_SHORT );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "PLATFORM_ARCH_ALT", "x86_64" );
	map.put( "ARCH", "64" );
}

println '-------------------------------------'
println 'Evnironment Variables Added: ' + map;
println 'JOB_NAME: ' + JOB_NAME;
println '-------------------------------------'

return map;

hudson.tasks.Shell.command:
#!/bin/bash
# This shell script extracts the contents of the clFFT archive into the appropriate directories

echo [INFO]COPYARTIFACT_BUILD_NUMBER_CLFFT_L: ${COPYARTIFACT_BUILD_NUMBER_CLFFT_L}

clFFT_DEBUG_DIR=$WORKSPACE/thirdParty/${OSTYPE}/clFFT/${PLATFORM_ARCH_SHORT}/Debug

mkdir -pv "${clFFT_DEBUG_DIR}"
Retval=$?
if [ $Retval -ne 0 ]; then
	echo [ERR]Failed to create the directory: ${clFFT_DEBUG_DIR}
	exit $Retval
fi

echo [INFO]Calling tar -jxvf clfft-Debug-${OSTYPE}-*-${P}.tar.bz2 -C "${clFFT_DEBUG_DIR}"
tar -jxvf clfft-Debug-${OSTYPE}-*-${P}.tar.bz2 -C "${clFFT_DEBUG_DIR}"
if [ $? -eq 0 ]; then
	# Move the contents of the  clFFT-2.4.0-Linux-x64 directory to the ${clFFT_DEBUG_DIR} directory
	mv -vf ${clFFT_DEBUG_DIR}/clFFT-*-Linux-${PLATFORM_ARCH_SHORT}/* ${clFFT_DEBUG_DIR}
	pushd ${clFFT_DEBUG_DIR}
		rm -rvf clFFT-*-Linux-${PLATFORM_ARCH_SHORT}
	popd
	clFFT_RELEASE_DIR=$WORKSPACE/thirdParty/${OSTYPE}/clFFT/${PLATFORM_ARCH_SHORT}/Release
	mkdir -pv "${clFFT_RELEASE_DIR}"
	Retval=$?
	if [ $Retval -ne 0 ]; then
		echo [ERR]Failed to create the directory: ${clFFT_RELEASE_DIR}
		exit $Retval
	fi

	echo [INFO]Calling tar -jxvf clfft-Release-${OSTYPE}-*-${P}.tar.bz2 -C "${clFFT_RELEASE_DIR}"
	tar -jxvf clfft-Release-${OSTYPE}-*-${P}.tar.bz2 -C "${clFFT_RELEASE_DIR}"
	mv -vf ${clFFT_RELEASE_DIR}/clFFT-*-Linux-${PLATFORM_ARCH_SHORT}/* ${clFFT_RELEASE_DIR}
	pushd ${clFFT_RELEASE_DIR}
		rm -rvf clFFT-*-Linux-${PLATFORM_ARCH_SHORT}
	popd
fi

hudson.plugins.parameterizedtrigger.Pred.properties:
