info.groovyScriptContent:
def map = [:] ;
def value=0;

PRODUCT_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "." + BUILD_NUMBER;
map.put( "PRODUCT_VERSION", PRODUCT_VERSION );

def ARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER ;
if ( "0" != SUBMINOR_NUMBER ) {
    ARTIFACT_PRODUCTVERSION+="-" + SUBMINOR_NUMBER;
}

ARTIFACT_PRODUCTVERSION+="." + BUILD_NUMBER + "-" + MILESTONE;
map.put( "ARTIFACT_PRODUCTVERSION", ARTIFACT_PRODUCTVERSION );

println 'MAP: ' + map;

return map;

