@echo off
setlocal enabledelayedexpansion

echo JOB_NAME: %JOB_NAME%

:: Parse the Job Name
for /f "tokens=2 delims=/" %%a in ('echo !JOB_NAME!') do (
	set CONFIGURATION=%%a
)

echo CONFIGURATION: %CONFIGURATION%

for /f "tokens=1,2,3,4 delims=," %%a in ('echo !CONFIGURATION!') do (
	set CONFIG=%%a
	set PLATFORM=%%c
	set PLATFORMTOOLSET=%%d
)

echo %CONFIG%
echo %PLATFORM%
echo %PLATFORMTOOLSET%

for /f "tokens=2 delims==" %%a in ('echo !CONFIG!') do (
	SET CONFIG=%%a
)

echo Configration: %CONFIG%

for /f "tokens=2 delims==" %%a in ('echo !PLATFORM!') do (
	SET PLATFORM=%%a
)

echo Platform: %PLATFORM%

for /f "tokens=2 delims==" %%a in ('echo !PLATFORMTOOLSET!') do (
	SET PLATFORMTOOLSET=%%a
)

echo PlatformToolset: %PLATFORMTOOLSET%

"%SEVENZ%" a MediaSDK-Binaries-%C%-%P%-%T%-%ARTIFACT_PRODUCTVERSION%.7z -r samples\*.exe samples\*.manifest samples\*.dll
"%SEVENZ%" a MediaSDK-lib-%C%-%P%-%T%-%ARTIFACT_PRODUCTVERSION%.7z -r samples\*.lib -xr^^!*.exp

endlocal
