def map = [:] ;

map.put( "PRODUCTVERSION", MAJOR_NUMBER + "." + MINOR_NUMBER + "." + BUILD_NUMBER );
map.put( "J", "1.8" );
map.put( "ARCHIVE_EXTENSION", "7z" );

if( JOB_NAME.contains( "Win32" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x86" );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "ARCH", "32" );
}

if( JOB_NAME.contains( "x64" ) ) {
	map.put( "PLATFORM_ARCH_SHORT", "x64" );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86_64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "ARCH", "64" );
}

String strVSName = "VS2013";
String strVSNameSmall = "vs13";
String strVSNameCaps = "VS13";

if ( JOB_NAME.contains( "v120" ) ) {
	strVSName = "VS2013";
	strVSNameSmall = "vs13";
	strVSNameCaps = "VS13";
	map.put( "VCTOOLKIT_SHORT", "vc12" );
	map.put( "VSKEY", "12.0" );
	map.put( "BOOST_VS", "msvc-12.0" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	strVSName = "VS2012";
	strVSNameSmall = "vs12";
	strVSNameCaps = "VS12";
	map.put( "VCTOOLKIT_SHORT", "vc11" );
	map.put( "VSKEY", "11.0" );
	map.put( "BOOST_VS", "msvc-11.0" );
}

if ( JOB_NAME.contains( "v100" ) ) {
	strVSName = "VS2010";
	strVSNameSmall = "vs10";	
	strVSNameCaps = "VS10";
	map.put( "VCTOOLKIT_SHORT", "vc10" );
	map.put( "VSKEY", "10.0" );
	map.put( "BOOST_VS", "msvc-10.0" );
}

if ( JOB_NAME.contains( "Debug" ) ) {
	map.put( "C_SC", "dbg" );
	map.put( "C_SC_FULL", "debug" );
}
if ( JOB_NAME.contains( "Release" ) ) {
	map.put( "C_SC", "rel" );
	map.put( "C_SC_FULL", "release" );
}

map.put( "VSNAME_SMALL", strVSNameSmall );
map.put( "VSNAME_CAPS", strVSNameCaps );
map.put( "VSNAME", strVSName );


println '---------------------------------';
println map;
println '---------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not found.
	exit /b 1
)

endlocal

@echo off
setlocal

REM This script extract the required boost headers and libraries.
REM Note: As of now now common uses the statically linked runtime shared libraries, so we extract only that.

REM Extract the boost includes.

set THIRDPARTY_DIR=InstallManagerExtn\3rdParty
if NOT EXIST "%THIRDPARTY_DIR%" (
	mkdir "%THIRDPARTY_DIR%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %THIRDPARTY_DIR%
		exit /b ERRORLEVEL
	)
)

echo [INFO][%~n0%~x0]Calling %SEVENZ% x -aoa boost-includes-*.%ARCHIVE_EXTENSION% -o"%THIRDPARTY_DIR%\boost"
%SEVENZ% x -aoa boost-includes-*.%ARCHIVE_EXTENSION% -o"%THIRDPARTY_DIR%\boost" | findstr /b /r /c:"\<Everything is Ok" /c:"\<Processing archive"
if ERRORLEVEL 1 (
	echo [ERR]Failed to extract boost-includes-*.%ARCHIVE_EXTENSION% 
	exit /b ERRORLEVEL
)

REM Extract the boost Libraries. 
echo [INFO][%~n0%~x0]Calling %SEVENZ% x -aoa boost-lib-link-static-runtime-shared-%C%-%T%-%PLATFORM_ARCH_SHORT%-*.%ARCHIVE_EXTENSION% -i!boost_*\lib\%P%\%C%\libboost_thread*.lib -i!boost_*\lib\%P%\%C%\libboost_system*.lib -o"%THIRDPARTY_DIR%\boost"
%SEVENZ% x -aoa boost-lib-link-static-runtime-shared-%C%-%T%-%PLATFORM_ARCH_SHORT%-*.%ARCHIVE_EXTENSION% -i!boost_*\lib\%P%\%C%\libboost_thread*.lib -i!boost_*\lib\%P%\%C%\libboost_system*.lib -o"%THIRDPARTY_DIR%\boost" | findstr /b /r /c:"\<Everything is Ok" /c:"\<Processing archive"
if ERRORLEVEL 1 (
	echo [ERR]Failed to extract libboost_thread*.lib from boost-lib-link-static-runtime-shared-%C%-%T%-%PLATFORM_ARCH_AMD%-*.%ARCHIVE_EXTENSION%
	exit /b ERRORLEVEL
)

pushd "%WORKSPACE%\InstallManagerExtn\UnifiedInstaller"
	%PERL% -pi.bak -e "s!PRODUCTION_URL_VERSION!%PRODUCTION_URL_VERSION%!g;" HttpDownload.cpp
	if ERRORLEVEL 1 (
		echo [ERR]Failed to replace PRODUCTION_URL_VERSION with %PRODUCTION_URL_VERSION% in the file HttpDownload.cpp
		popd
		exit /b ERRORLEVEL
	)
	%PERL% -pi.bak -e "s!ARTIFACT_PRODUCTVERSION!%APPSDK_INSTALLER_ARTIFACT_PRODUCTVERSION%!g;" HttpDownload.cpp
	if ERRORLEVEL 1 (
		echo [ERR]Failed to replace ARTIFACT_PRODUCTVERSION with %APPSDK_INSTALLER_ARTIFACT_PRODUCTVERSION% in the file HttpDownload.cpp
		popd
		exit /b ERRORLEVEL
	)
popd

endlocal

@echo off
::- Copyright ©2014 Advanced Micro Devices, Inc. All rights reserved.

setlocal EnableDelayedExpansion

call "%ProgramFiles(x86)%\Microsoft Visual Studio 11.0\Common7\Tools\VsDevCmd.bat"
set VERPATCH=%WORKSPACE%\verpatch\verpatch.exe
set SAMPLEDIR=%WORKSPACE%\InstallManagerExtn\%C%\%P%
set COPYRIGHT="Copyright (c), %DATE:~-4%. Advanced Micro Devices, Inc."

echo [INFO]Adding version information and signing binaries in: %SAMPLEDIR%

::We set and sign all files with the default version
::The variable PRODUCTVERSION, FILEVERSION  is supposed to be set by the Jenkins job with appropriate value.
set BASEFILEVERSION=%PRODUCTVERSION%
set FILEVERSION=%BASEFILEVERSION%.0

for /r "%SAMPLEDIR%" %%G in (*.exe) do (
	"%VERPATCH%" "%%~G" "%FILEVERSION%" /va /s FileDescription "%%~nxG" /s LegalCopyright %COPYRIGHT% /s ProductName "%PRODUCTNAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Could not add version resource information to %%~G
	)
	"%VERPATCH%" "%%~G" /pv "%FILEVERSION%"
	if ERRORLEVEL 1 (
		echo [ERR]Could not Update the ProductVersion information in %%~G
	)
	
	echo [INFO]Calling signtool sign /f %WORKSPACE%\AMDCert_exp6-2016.pfx /p %SIGN_PWD% "%%~G"
	signtool sign /f %WORKSPACE%\AMDCert_exp6-2016.pfx /p %SIGN_PWD% "%%~G"
	if ERRORLEVEL 1 (
		echo [ERR]Could not sign %%~G
	) else (
		::Timestamp it
		echo [INFO]Calling signtool timestamp /t %TIMESTAMP_URL% "%%~G"
		signtool timestamp /t %TIMESTAMP_URL% "%%~G"
	)
)

for /r "%SAMPLEDIR%" %%G in (*.dll) do (
	"%VERPATCH%" "%%~G" "%FILEVERSION%" /va /s FileDescription "%%~nxG" /s LegalCopyright %COPYRIGHT% /s ProductName "%PRODUCTNAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Could not add version resource information to %%~G
	)
	"%VERPATCH%" "%%~G" /pv "%FILEVERSION%"
	if ERRORLEVEL 1 (
		echo [ERR]Could not Update the ProductVersion information in %%~G
	)
	
	echo [INFO]Calling signtool sign /f %WORKSPACE%\AMDCert_exp6-2016.pfx /p %SIGN_PWD% "%%~G"
	signtool sign /f %WORKSPACE%\AMDCert_exp6-2016.pfx /p %SIGN_PWD% "%%~G"
	if ERRORLEVEL 1 (
		echo [ERR]Could not sign %%~G
	) else (
		::Timestamp it
		echo [INFO]Calling signtool timestamp /t %TIMESTAMP_URL% "%%~G"
		signtool timestamp /t %TIMESTAMP_URL% "%%~G"
	)
)

endlocal
