import java.lang.String;

def map = [:] ;

def PRODUCT_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "." + BUILD_NUMBER + ".0";
map.put( "PRODUCT_VERSION", PRODUCT_VERSION );

def ARTIFACT_PRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER ;
if ( "0" != SUBMINOR_NUMBER ) {
    ARTIFACT_PRODUCTVERSION+="-" + SUBMINOR_NUMBER;
}

def PRODUCT_NAME="AMD Compute SDK Installer " + ARTIFACT_PRODUCTVERSION;
map.put( "ProductName", PRODUCT_NAME );

def PRODUCTION_URL_VERSION = ARTIFACT_PRODUCTVERSION + "-" + MILESTONE;
map.put( "PRODUCTION_URL_VERSION", PRODUCTION_URL_VERSION );

ARTIFACT_PRODUCTVERSION+="." + BUILD_NUMBER + "-" + MILESTONE;
map.put( "ARTIFACT_PRODUCTVERSION", ARTIFACT_PRODUCTVERSION );

if( JOB_NAME.contains( "x86" ) ) {
	map.put( "PLATFORM_ARCH_VS", "Win32" );
	map.put( "PLATFORM_ARCH_INTEL", "ia32" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86" );
	map.put( "PLATFORM_ARCH_AMD", "x86" );
	map.put( "ARCH", "32" );
}

if( JOB_NAME.contains( "x64" ) ) {
	map.put( "PLATFORM_ARCH_VS", "x64" );
	map.put( "PLATFORM_ARCH_INTEL", "intel64" );
	map.put( "PLATFORM_ARCH_INTEL_ALT", "x86_64" );
	map.put( "PLATFORM_ARCH_AMD", "amd64" );
	map.put( "ARCH", "64" );
}

if ( JOB_NAME.contains( "v120" ) ) {
	map.put( "VSNAME", "VS2013" );
	map.put( "VCNAME_FULL", "Visual C++ 2013" );
	map.put( "VCTOOLKIT_SHORT", "vc12" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	map.put( "VSNAME", "VS2012" );
	map.put( "VCNAME_FULL", "Visual C++ 2012" );
	map.put( "VCTOOLKIT_SHORT", "vc11" );
}

if ( JOB_NAME.contains( "v100" ) ) {
	map.put( "VSNAME", "VS2010" );
	map.put( "VCNAME_FULL", "Visual C++ 2010" );
	map.put( "VCTOOLKIT_SHORT", "vc10" );
}

println '---------------------------------';
println map;
println '---------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

set J=%APARAPI_JDK%

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not found in %WORKSPACE%
	exit /b 1
)

endlocal

@echo off

setlocal

echo APPSDK_PKG_MM_PB_W: %COPYARTIFACT_BUILD_NUMBER_APPSDK_PKG_MM_PB_W%
echo APPSDK_PKG_MM_SRC_W: %COPYARTIFACT_BUILD_NUMBER_APPSDK_PKG_MM_SRC_W%
echo APPSDK_PKG_MM_PB_APARAPI_W: %COPYARTIFACT_BUILD_NUMBER_APPSDK_PKG_MM_PB_APARAPI_W%
echo SDKInstallManager_BUILD: %COPYARTIFACT_BUILD_NUMBER_SDKInstallManager_BUILD%
echo MEDIASDK_PKG: %COPYARTIFACT_BUILD_NUMBER_MEDIASDK_PKG%

echo APPSDK_DIR_VERSION: %APPSDK_DIR_VERSION%
echo APPSDK_METRIC_VERSION: %APPSDK_METRIC_VERSION%

%SEVENZ% x -aoa SDKInstallManager_Release_Win32_*_bin.7z -i!MSICA.dll -o"%WORKSPACE%\InstallManagerExtn\Release\Win32"

if ERRORLEVEL 1 (
	exit /b ERRORLEVEL
)

if Not exist MM (
	mkdir MM
)

REM Copy the Sources msm
for /r %%i in (*Sources.msm) do (
	if Not exist MM\%%~nxi (
		echo Copying %%~ni to MM
		copy /Y /V "%%i" MM
	) else (
		echo Skipping %%~nxi already copied.
	)
)


REM Extract the Prebuilt Merge modules from the archives (ComputeSDK_PB_MM_C++Amp_Release_x64_v110_3.0.175.25-ALPHA.7z)
for /r %%i in (ComputeSDK_PB_MM_*_%C%_%PLATFORM_ARCH_VS%_%T%_*.7z) do (
	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
	if ERRORLEVEL 1 (
		exit /b ERRORLEVEL
	)
)

REM Extract the MediaSDK Package Merge modules from the archives (MediaSDK-v1.1.334.21-Beta-windows-neutral-MM.7z)
for /r %%i in (MediaSDK-*-%OSTYPE%-*-MM.7z) do (
	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
	if ERRORLEVEL 1 (
		exit /b ERRORLEVEL
	)
)

REM Extract the ComputeSDK_PB_MM_APARAPI_%T%_JDK%APARAPI_JDK%_*.7z
for /r %%i in (ComputeSDK_PB_MM_APARAPI_%T%_JDK%APARAPI_JDK%_*.7z) do (
	%SEVENZ% x -aoa "%%i" -o"%WORKSPACE%\MM"
	if ERRORLEVEL 1 (
		exit /b ERRORLEVEL
	)
)

endlocal

@echo off
setlocal

set CABDIR=%WORKSPACE%\%P%\W\DiskImages\DISK1

if defined PRODUCTION_WEB (
	if "%PRODUCTION_WEB%"=="true" (
		::Create an archive of the web components
		%SEVENZ% a AMD-ComputeSDKInstaller-v%ARTIFACT_PRODUCTVERSION%-%OSTYPE%-%M%-components-%P%.7z .\%P%\W\DiskImages\DISK1\*.cab .\%P%\W\DiskImages\DISK1\*.CAB .\%P%\W\DiskImages\DISK1\*.msi
	)
)

endlocal
