import java.lang.String;

//Dump the environment variables
def map = [:];

def ARCHIVE_EXTENSION = "7z";

if( JOB_NAME.contains( "LBM" ) ) {
	ARCHIVE_EXTENSION = "tar.bz2";
}

map.put( "ARCHIVE_EXTENSION", ARCHIVE_EXTENSION );

if (JOB_NAME.contains( "EG" ) ) {
	map.put( "VDK_FULL_NAME", "EmbeddedGaming" );
	map.put( "VDK_FULL_DISPLAY_NAME", "Embedded Gaming" );
}

if ( JOB_NAME.contains( "US" ) ) {
	map.put( "VDK_FULL_NAME", "Ultrasound" );
	map.put( "VDK_FULL_DISPLAY_NAME", "Ultrasound" );
}

map.put( "J", "1.8" );
map.put( "V", "EG" );

def VDK_PRODUCT_VERSION_DISPLAY = MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER;
if( JOB_NAME.contains( "LBM" ) ) {
	VDK_PRODUCT_VERSION_DISPLAY += "-" + MILESTONE;
}
else {
	VDK_PRODUCT_VERSION_DISPLAY += " " + MILESTONE;
}
map.put( "VDK_PRODUCT_VERSION_DISPLAY", VDK_PRODUCT_VERSION_DISPLAY );

def VDK_ARTIFACTDISPLAY_NUMBER = MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + "." + VDK_EG_BUILD_NUMBER + "." + BUILD_NUMBER;
if( JOB_NAME.contains( "LBM" ) ) {
	VDK_ARTIFACTDISPLAY_NUMBER += "-" + MILESTONE;
}
else {
	VDK_ARTIFACTDISPLAY_NUMBER += " " + MILESTONE;
}

map.put( "VDK_ARTIFACTDISPLAY_NUMBER", VDK_ARTIFACTDISPLAY_NUMBER );

def parPRODUCTVERSION;
try {
	parPRODUCTVERSION = PRODUCTVERSION;
	println 'PRODUCTVERSION found: ' + PRODUCTVERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//PRODUCTVERSION not present
	println 'PRODUCTVERSION NOT found';
	parPRODUCTVERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "." + VDK_EG_BUILD_NUMBER + "." + BUILD_NUMBER;
	map.put( "PRODUCTVERSION", parPRODUCTVERSION );
}

def parVDK_EG_DIR_VERSION;
try {
	parVDK_EG_DIR_VERSION = VDK_EG_DIR_VERSION;
	println 'VDK_EG_DIR_VERSION found: ' + VDK_EG_DIR_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'VDK_EG_DIR_VERSION NOT found';
	parVDK_EG_DIR_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "PR" != MILESTONE ) {
		parVDK_EG_DIR_VERSION += "-" + MILESTONE;
	}

	map.put( "VDK_EG_DIR_VERSION", parVDK_EG_DIR_VERSION );	
}

def parVDK_EG_METRIC_VERSION;
try {
	parVDK_EG_METRIC_VERSION = VDK_EG_METRIC_VERSION;
	println 'VDK_EG_METRIC_VERSION found: ' + VDK_EG_METRIC_VERSION;
}
catch( groovy.lang.MissingPropertyException e ) {
	//ARTIFACT_PRODUCT not present
	println 'VDK_EG_METRIC_VERSION NOT found';
	parVDK_EG_METRIC_VERSION=MAJOR_NUMBER + "." + MINOR_NUMBER + "-" + SUBMINOR_NUMBER;
	if ( "PR" != MILESTONE ) {
		parVDK_EG_METRIC_VERSION += "-" + MILESTONE;
	}

	parVDK_EG_METRIC_VERSION += "L";

	map.put( "VDK_EG_METRIC_VERSION", parVDK_EG_METRIC_VERSION );	
}

println '--------------------------------------------------------------';
println 'JOB_NAME: ' + JOB_NAME;
println 'BUILD_NUMBER: ' + BUILD_NUMBER;
println 'Environment variables inserted: ' + map;
println '--------------------------------------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

echo P: %P%
echo J: %J%
echo T: %T%

if EXIST Environ.bat (
	call Environ.bat
) else (
	echo [ERR]File Environ.bat not found.
	exit /b 1
)

endlocal

@echo off

setlocal

echo COPYARTIFACT_BUILD_NUMBER_VDK_EG_BUILD: %COPYARTIFACT_BUILD_NUMBER_VDK_EG_BUILD%

REM Extract the archives VDK_$V_*_$T_libs_*.7z,VDK_$V_*_sources_*.7z
for /r %%i in (VDK_%V%_*_%OSTYPE%_*.%ARCHIVE_EXTENSION%) do (
	%SEVENZ% x -aoa "%%i"
	if Not ERRORLEVEL 0 (
		exit /b ERRORLEVEL
	)
)

endlocal

@echo off
setlocal EnableDelayedExpansion

REM Extract the binary archives VDK_%V%_Release_*_bin_*.%ARCHIVE_EXTENSION%
for /r %%i in (VDK_%V%_Release_*_bin_*_${OSTYPE}_*.%ARCHIVE_EXTENSION%) do (
	%SEVENZ% x -aoa "%%i"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to extract archive %%i
		exit /b ERRORLEVEL
	)
)

::- Now update the bin directory
set SAMPLES_DIR=%WORKSPACE%\VDK\%VDK_FULL_NAME%\%VDK_PRODUCT_VERSION_DISPLAY%\samples
set BIN_DIR=%SAMPLES_DIR%\bin
set X86BIN_DIR=%BIN_DIR%\x86
set X86_64BIN_DIR=%BIN_DIR%\x86_64

set VS12x86_DIR=%X86BIN_DIR%\vs12
set VS12x86_64_DIR=%X86_64BIN_DIR%\vs12
set VS10x86_DIR=%X86BIN_DIR%\vs10
set VS10x86_64_DIR=%X86_64BIN_DIR%\vs10

set DIR_NAME=%VS10x86_DIR%
if NOT EXIST "%DIR_NAME%" (
	mkdir "%DIR_NAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %DIR_NAME%
		exit /b ERRORLEVEL
	)
)

set DIR_NAME=%VS12x86_DIR%
if NOT EXIST "%DIR_NAME%" (
	mkdir "%DIR_NAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %DIR_NAME%
		exit /b ERRORLEVEL
	)
)

set DIR_NAME=%VS10x86_64_DIR%
if NOT EXIST "%DIR_NAME%" (
	mkdir "%DIR_NAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %DIR_NAME%
		exit /b ERRORLEVEL
	)
)

set DIR_NAME=%VS12x86_64_DIR%
if NOT EXIST "%DIR_NAME%" (
	mkdir "%DIR_NAME%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %DIR_NAME%
		exit /b ERRORLEVEL
	)
)

::- Now extract the bin archives to the appropriate folders

::The bin are in the following directory structure
::Folder PATH listing
REM %WORKSPACE%
REM ----VDK
    REM ----EmbeddedGaming
        REM ----1.0-1 PR
            REM ----samples
                REM ----qvaBlend
                REM |   ----bin
                REM |       ----x86
                REM |           ----Release_vs12
                REM ----qvaDecoderTest
                REM |   ----bin
                REM |       ----x86
                REM |           ----Release_vs12
                REM ----qvaEncoderTest
                REM |   ----bin
                REM |       ----x86
                REM |           ----Release_vs12
                REM ----qvaPlayback
                REM |   ----bin
                REM |       ----x86
                REM |           ----Release_vs12
                REM ----sha256
                    REM ----bin
                        REM ----x86
                            REM ----Release_vs12

:: We need to remove the directory named after the sample and remove the Release_ word from the directory
:: samples\bin\x86\vs10

set SourcePath=
set TargetBinDir=

for /r "%SAMPLES_DIR%" %%G in (*.exe) do (
	set SourcePath=%%~pG
	set PERFORM=
	if !SourcePath:~-17!==x86\Release_vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_DIR
		set TargetDir=%VS12x86_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-17!==x86\Release_vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_DIR
		set TargetDir=%VS10x86_DIR%\%%~nxG
		set PERFORM=true
	)
	
	if !SourcePath:~-20!==x86_64\Release_vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_64_DIR
		set TargetDir=%VS12x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-20!==x86_64\Release_vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_64_DIR
		set TargetDir=%VS10x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	
	
	if defined PERFORM (
		echo [INFO]Moving %%G to !TargetDir!
		move /y "%%G" "!TargetDir!"
		if ERRORLEVEL 1 (
			echo [ERR]Failed to move %%G to !TargetDir!
			exit /b ERRORLEVEL
		)
	) else (
		echo [INFO]Skipping moving %%G 
	)
)

set MODULES_DIR=%WORKSPACE%\VDK\%VDK_FULL_NAME%\%VDK_PRODUCT_VERSION_DISPLAY%\modules
for /r "%MODULES_DIR%" %%G in (*.dll) do (
	set SourcePath=%%~pG
	set PERFORM=
	if !SourcePath:~-17!==x86\Release_vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_DIR
		set TargetDir=%VS12x86_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-17!==x86\Release_vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_DIR
		set TargetDir=%VS10x86_DIR%\%%~nxG
		set PERFORM=true
	)
	
	if !SourcePath:~-20!==x86_64\Release_vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_64_DIR
		set TargetDir=%VS12x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-20!==x86_64\Release_vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_64_DIR
		set TargetDir=%VS10x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	
	if defined PERFORM (
		echo [INFO]Copying %%G to !TargetDir!
		copy /Y /V "%%G" "!TargetDir!"
		if ERRORLEVEL 1 (
			echo [ERR]Failed to move %%G to !TargetDir!
			exit /b ERRORLEVEL
		)
	) else (
		echo [INFO]Skipping copying %%G 
	)
)

pushd "%SAMPLES_DIR%"
	for /f %%G in ('dir /b /a:d .\') do (
		if NOT "%%G"=="bin" (
			echo [INFO]Removing directory %SAMPLES_DIR%\%%G\bin recursively
			rmdir /S /Q "%SAMPLES_DIR%\%%G\bin"
			if ERRORLEVEL 1 (
				echo [ERR]Could not remove directory %SAMPLES_DIR%\%%G\bin
				exit /b ERRORLEVEL
			)
		)
	)
popd

::-Now we need to copy the 3rdParty dlls to the appropriate folders in the bin directory
set THIRDPARTY_DIR=%WORKSPACE%\VDK\%VDK_FULL_NAME%\%VDK_PRODUCT_VERSION_DISPLAY%\thirdParty

::-Now iterate over the above directory looking for dlls
for /r "%THIRDPARTY_DIR%" %%G in (*.dll) do (
	set SourcePath=%%~pG
	set PERFORM=
	if !SourcePath:~-9!==x86\vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_DIR
		set TargetDir=%VS12x86_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-9!==x86\vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_DIR
		set TargetDir=%VS10x86_DIR%\%%~nxG
		set PERFORM=true
	)
	
	if !SourcePath:~-12!==x86_64\vs12\ ( 
		:: 32-bit VS 12 bin, move to VS12x86_64_DIR
		set TargetDir=%VS12x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	if !SourcePath:~-12!==x86_64\vs10\ ( 
		:: 32-bit VS 10 bin, move to VS10x86_64_DIR
		set TargetDir=%VS10x86_64_DIR%\%%~nxG
		set PERFORM=true
	)
	
	if defined PERFORM (
		echo [INFO]Copying %%G to !TargetDir!
		copy /Y /V "%%G" "!TargetDir!"
		if ERRORLEVEL 1 (
			echo [ERR]Failed to move %%G to !TargetDir!
			exit /b ERRORLEVEL
		)
	) else (
		echo [INFO]Skipping copying %%G
	)
)

endlocal

hudson.tasks.Shell.command:
#!/bin/bash

SH_NAME=$(basename "${BASH_SOURCE}")

echo [${SH_NAME}][${LINENO}][INFO]This script extracts the build artifacts
#VDK_${V}_Release_*_bin_${OSTYPE}_*.${ARCHIVE_EXTENSION},VDK_${V}_sources_${OSTYPE}_*.${ARCHIVE_EXTENSION}

ARCHIVE_NAME=$(readlink -f VDK_${V}_Release_*_bin_${OSTYPE}_*.${ARCHIVE_EXTENSION})
echo [${SH_NAME}][${LINENO}][INFO]Executing tar -jxvf ${ARCHIVE_NAME} --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/build" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/config" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/src"

tar  --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/build" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/config" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/src" -jxvf ${ARCHIVE_NAME}
Retval=$?
if [ $Retval -ne 0 ]; then
	echo [${SH_NAME}][${LINENO}][ERR]Failed to extract ${ARCHIVE_NAME}
	exit $Retval
fi

ARCHIVE_NAME=$(readlink -f VDK_${V}_sources_${OSTYPE}_*.${ARCHIVE_EXTENSION})
echo [${SH_NAME}][${LINENO}][INFO]Executing tar -jxvf ${ARCHIVE_NAME}
tar --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/build" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/config" --exclude="VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/modules/QvaDecoder/src" --exclude=VDK/${VDK_FULL_NAME}/$VDK_PRODUCT_VERSION_DISPLAY/thirdParty/*/*/windows -jxvf ${ARCHIVE_NAME} 
Retval=$?
if [ $Retval -ne 0 ]; then
	echo [${SH_NAME}][${LINENO}][ERR]Failed to extract ${ARCHIVE_NAME}
fi

exit $Retval
