hudson.tasks.BatchFile.command:
@echo off setlocal
echo SEVENZ=%SEVENZ%
%SEVENZ% x -aoa boost/%BOOST_VERSION%/boost_%BOOST_VERSION%.7z -xr!doc -xr!more -xr!tools/auto_index -xr!tools/bcp -xr!tools/boostbook -xr!tools/boostdep -xr!tools/inspect -xr!tools/litre -xr!tools/quickbook -xr!tools/regression -xr!tools/build/doc -xr!tool/build/example -xr!tools/build/notes -xr!tools/build/scripts -xr!tools/build/test -xr!tools/build/website -xr!tools/build/src/build -xr!tools/build/src/contrib -xr!tools/build/src/kernel -xr!tools/build/src/options -xr!tools/build/src/tools -xr!tools/build/src/util
if ERRORLEVEL 1 ( echo [FAILURE]: Failed to extract boost/%BOOST_VERSION%/boost_%BOOST_VERSION%.7z exit /b ERRORLEVEL )
pushd boost_%BOOST_VERSION% call bootstrap.bat popd
endlocal

@echo off setlocal
pushd boost_%BOOST_VERSION% echo [INFO] Calling .\b2 --toolset=%T% variant=%C% threading=multi link=%LL% runtime-link=%R% address-model=%A% --stagedir=lib\%P%\%C% -j %NUMBER_OF_PROCESSORS% -q --hash .\b2 --toolset=%T% variant=%C% threading=multi link=%LL% runtime-link=%R% address-model=%A% --stagedir=lib\%P%\%C% -j %NUMBER_OF_PROCESSORS% -q --hash popd
endlocal

@echo off
setlocal

if EXIST artifacts (
	::-Empty it
	rmdir /S /Q artifacts
	if ERRORLEVEL 1 (
		echo [FAILURE]: Could not delete the artifacts directory
		exit /b ERRORLEVEL
	)
)

mkdir artifacts\boost_%BOOST_VERSION%\inc
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to create the directory artifacts\boost_%BOOST_VERSION%\inc
	exit /b ERRORLEVEL
)
move /Y boost_%BOOST_VERSION%\boost artifacts\boost_%BOOST_VERSION%\inc
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to move boost_%BOOST_VERSION%\boost to artifacts\boost_%BOOST_VERSION%\inc
	exit /b ERRORLEVEL
)

REM Create the include file archive. This we do only for C=release, LL=shared, A=64 and T=msvc-12.0
REM Since the headers are all common
if "%C%"=="release" (
	if "%LL%"=="shared" (
		if "%A%"=="64" (
			if "%T%"=="msvc-12.0" (
				if "%L%"=="WBM" (
					pushd artifacts
						%SEVENZ% a -t7z "%WORKSPACE%\boost-includes-%BOOST_VERSION%-%BUILD_NUMBER%.7z" -ir!boost_%BOOST_VERSION%\inc
					popd
				)
			)
		)
	)
)

mkdir artifacts\boost_%BOOST_VERSION%\lib\%Platform%\%Configuration%
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to create the directory artifacts\boost_%BOOST_VERSION%\lib\%Platform%\%Configuration%
	exit /b ERRORLEVEL
)

move /Y boost_%BOOST_VERSION%\lib\%C%\lib\*.lib artifacts\boost_%BOOST_VERSION%\lib\%Platform%\%Configuration%
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to move boost_%BOOST_VERSION%\lib\%C%\lib\*.lib to artifacts\boost_%BOOST_VERSION%\lib\%Platform%\%Configuration%inc
	exit /b ERRORLEVEL
)

if "%LL%"=="shared" (
	mkdir artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
	if ERRORLEVEL 1 (
		echo [FAILURE]: Failed to create the directory artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
		exit /b ERRORLEVEL
	)
	move /Y boost_%BOOST_VERSION%\lib\%C%\lib\*.dll artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
	if ERRORLEVEL 1 (
		echo [FAILURE]: Failed to move boost_%BOOST_VERSION%\lib\%C%\lib\*.dll to artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
		exit /b ERRORLEVEL
	)
	
	pushd boost_%BOOST_VERSION%\bin.v2\libs
		for /r %%i in (*.pdb) do (
			move /Y %%i .\..\..\..\artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
			if ERRORLEVEL 1 (
				echo [FAILURE]: Failed to move %%i to .\..\..\..\artifacts\boost_%BOOST_VERSION%\bin\%Platform%\%Configuration%
				exit /b ERRORLEVEL
			)
		)
	popd
	
	rmdir /S /Q boost_%BOOST_VERSION%\bin.v2
	if ERRORLEVEL 1 (
		echo [WARN]: Failed to delete the directory boost_%BOOST_VERSION%\bin.v2
	)
	
	pushd artifacts
		%SEVENZ% a -t7z "%WORKSPACE%\boost-binaries-link-%LL%-runtime-%R%-%Configuration%-%PlatformToolset%-%PLATFORM_ARCH_SHORT%-%BOOST_VERSION%-%BUILD_NUMBER%.7z" -ir!boost_%BOOST_VERSION%\bin
	popd
)

pushd artifacts
	%SEVENZ% a -t7z "%WORKSPACE%\boost-lib-link-%LL%-runtime-%R%-%Configuration%-%PlatformToolset%-%PLATFORM_ARCH_SHORT%-%BOOST_VERSION%-%BUILD_NUMBER%.7z" -ir!boost_%BOOST_VERSION%\lib
popd

endlocal
