import java.lang.String;

//Dump the environment variables
def map = [:];

map.put( "ARCHIVE_EXTENSION", "7z" );
map.put( "VDK_FULL_NAME", "iCafe" );
map.put( "VDK_FULL_DISPLAY_NAME", "iCafe" );
map.put( "J", "1.8" );
map.put( "V", "iCafe" );
map.put( "P", "x64" );

String strVSName = "VS2013";
String strVSNameSmall = "vs13";
String strVSNameCaps = "VS13"; 

if ( JOB_NAME.contains( "v120" ) ) {
	strVSName = "VS2013";
	strVSNameSmall = "vs13";
	strVSNameCaps = "VS13";
	map.put( "VCTOOLKIT_SHORT", "vc12" );
	map.put( "VSKEY", "12.0" );
	map.put( "BOOST_VS", "msvc-12.0" );
}

if ( JOB_NAME.contains( "v110" ) ) {
	strVSName = "VS2012";
	strVSNameSmall = "vs12";
	strVSNameCaps = "VS12";
	map.put( "VCTOOLKIT_SHORT", "vc11" );
	map.put( "VSKEY", "11.0" );
	map.put( "BOOST_VS", "msvc-11.0" );
}

if ( JOB_NAME.contains( "v100" ) ) {
	strVSName = "VS2010";
	strVSNameSmall = "vs10";	
	strVSNameCaps = "VS10";
	map.put( "VCTOOLKIT_SHORT", "vc10" );
	map.put( "VSKEY", "10.0" );
	map.put( "BOOST_VS", "msvc-10.0" );
}

map.put( "VSNAME_SMALL", strVSNameSmall );
map.put( "VSNAME_CAPS", strVSNameCaps );
map.put( "VSNAME", strVSName );

def VDK_PRODUCT_VERSION_DISPLAY = MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + " " + MILESTONE;
map.put( "VDK_PRODUCT_VERSION_DISPLAY", VDK_PRODUCT_VERSION_DISPLAY );

def VDK_ARTIFACTDISPLAY_NUMBER = MAJOR_NUMBER + "." + MINOR_NUMBER  + "-" + SUBMINOR_NUMBER + "." + iCafe_VDK_BUILD_NUMBER + "." + BUILD_NUMBER + "-" + MILESTONE;
map.put( "VDK_ARTIFACTDISPLAY_NUMBER", VDK_ARTIFACTDISPLAY_NUMBER );

println '--------------------------------------------------------------';
println 'JOB_NAME: ' + JOB_NAME;
println 'BUILD_NUMBER: ' + BUILD_NUMBER;
println 'Environment variables inserted: ' + map;
println '--------------------------------------------------------------';

return map;

hudson.tasks.BatchFile.command:
@echo off

setlocal

echo COPYARTIFACT_BUILD_NUMBER_ICAFE_VDK_BUILD: %COPYARTIFACT_BUILD_NUMBER_ICAFE_VDK_BUILD%
echo iCafe_VDK_BUILD_NUMBER: %iCafe_VDK_BUILD_NUMBER%

REM Extract the archives VDK_%V%_sources_%T%_*.7z
echo [INFO][%~n0%~x0]Calling %SEVENZ% x -aoa VDK_%V%_sources_%T%_*.%ARCHIVE_EXTENSION%
%SEVENZ% x -aoa VDK_%V%_sources_%T%_*.%ARCHIVE_EXTENSION%
if ERRORLEVEL 1 (
	echo [ERR][%~n0%~x0]Failed to extract VDK_%V%_sources*.%ARCHIVE_EXTENSION%
	exit /b ERRORLEVEL
)

REM Extract the thirdParty archives
for /r %%i in (VDK_%V%_3rdParty_*_%T%_*.%ARCHIVE_EXTENSION%) do (
	echo [INFO][%~n0%~x0]Calling %SEVENZ% x -aos "%%i"
	%SEVENZ% x -aos "%%i" | findstr /b /r /c:"\<Everything is Ok" /c:"\<Processing archive"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to extract archive %%i
		exit /b ERRORLEVEL
	)
)

endlocal

@echo off
setlocal EnableDelayedExpansion

::- Now update the bin directory
set SAMPLES_DIR=%WORKSPACE%\VDK\%VDK_FULL_NAME%\%VDK_PRODUCT_VERSION_DISPLAY%\samples
set BIN_DIR=%SAMPLES_DIR%\bin
set X86BIN_DIR=%BIN_DIR%\Win32
set X86_64BIN_DIR=%BIN_DIR%\x64

if NOT EXIST "%X86BIN_DIR%" (
	mkdir "%X86BIN_DIR%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %X86BIN_DIR%
		exit /b ERRORLEVEL
	)
)

if NOT EXIST "%X86_64BIN_DIR%" (
	mkdir "%X86_64BIN_DIR%"
	if ERRORLEVEL 1 (
		echo [ERR]Failed to create directory %X86_64BIN_DIR%
		exit /b ERRORLEVEL
	)
)

::The bin are in the following directory structure
::Folder PATH listing
REM %WORKSPACE%
REM ----VDK
    REM ----iCafe
        REM ----1.0-1 PR
            REM ----samples
                REM ----[SampleName]
                REM |   ----bin
                REM |       ----Win32
                REM |           ----Release
		REM |			----[Toolset]
				
REM Extract the binary archives VDK_%V%_Release_*_%T%_bin_*.%ARCHIVE_EXTENSION% VDK_iCafe_Release_x64_v120_bin_1.0-0.18-Alpha.7z
for /r %%i in (VDK_%V%_Release_Win32_%T%_bin_*.%ARCHIVE_EXTENSION%) do (
	pushd "%X86BIN_DIR%"
		%SEVENZ% e -aos "%%i"
		if ERRORLEVEL 1 (
			echo [ERR]Failed to extract archive %%i
			exit /b ERRORLEVEL
		)
	popd
)

for /r %%i in (VDK_%V%_Release_x64_%T%_bin_*.%ARCHIVE_EXTENSION%) do (
	pushd "%X86_64BIN_DIR%"
		%SEVENZ% e -aos "%%i"
		if ERRORLEVEL 1 (
			echo [ERR]Failed to extract archive %%i
			exit /b ERRORLEVEL
		)
	popd
)

REM Extract the pre-built driver archives
for /r %%i in (VDK_iCafe_W7*_x64_*_driver_*-*.%ARCHIVE_EXTENSION%) do (
	echo [INFO][%~n0%~x0]Calling %SEVENZ% x -aos "%%i" -ir^^!*.sys
	pushd "%X86_64BIN_DIR%"
		%SEVENZ% e -aos "%%i" -ir^^!*.sys
		if ERRORLEVEL 1 (
			echo [ERR]Failed to extract archive %%i
			exit /b ERRORLEVEL
		)
	popd
)

endlocal
