return map;

hudson.tasks.BatchFile.command:
@echo off
setlocal enabledelayedexpansion

::-Get the xerces version number from the archive xerces-src-3.1.1-22.7z
::-------------------------------------------
set XERCES_INC_ARCHIVE=
for /r %%i in (xerces-*-inc.%ARCHIVE_EXTENSION%) do (
	set XERCES_INC_ARCHIVE=%%~ni
)

echo [INFO]XERCES_INC_ARCHIVE: !XERCES_INC_ARCHIVE!

if NOT Exist "!XERCES_INC_ARCHIVE!" (
	mkdir "!XERCES_INC_ARCHIVE!"
)

echo XERCESCROOT=%WORKSPACE%\!XERCES_INC_ARCHIVE! > XercesEnviron.props
echo XALANCROOT=%WORKSPACE% >> XercesEnviron.props

endlocal

@echo off
setlocal

echo [INFO]MSBUILD: %MSBUILD%
echo [INFO]SEVENZ: %SEVENZ%
echo [INFO]XERCESCROOT: %XERCESCROOT%
echo [INFO]XALANCROOT: %XALANCROOT%
 
echo [INFO]Calling: %SEVENZ% x -aoa xerces-*-inc.%ARCHIVE_EXTENSION% -o"%XERCESCROOT%"
%SEVENZ% x -aoa xerces-*-inc.%ARCHIVE_EXTENSION% -o"%XERCESCROOT%"
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to extract xerces-*-inc.%ARCHIVE_EXTENSION%
	exit /b ERRORLEVEL
)

::- extract the binary xerces-bin-Debug-Win32-v120-3.1.1-22.%ARCHIVE_EXTENSION% into the XERCESCROOT directory
echo [INFO]Calling: %SEVENZ% x -aoa xerces-*-bin-%C%-%P%-%T%.%ARCHIVE_EXTENSION% -o"%XERCESCROOT%"
%SEVENZ% x -aoa xerces-*-bin-%C%-%P%-%T%.%ARCHIVE_EXTENSION% -o"%XERCESCROOT%"
if ERRORLEVEL 1 (
	echo [FAILURE]: Failed to extract xerces-*-bin-%C%-%P%-%T%.%ARCHIVE_EXTENSION%
	exit /b ERRORLEVEL
)

if NOT EXIST "%XALANCROOT%\Build\%XERCES_BUILD_ARCH%\%VCNAME_ALT%\%C%" (
	mkdir "%XALANCROOT%\Build\%XERCES_BUILD_ARCH%\%VCNAME_ALT%\%C%"
)

copy /V /Y "%XERCESCROOT%\Build\%XERCES_BUILD_ARCH%\%VCNAME_ALT%\%C%\*.dll" "%XALANCROOT%\Build\%XERCES_BUILD_ARCH%\%VCNAME_ALT%\%C%"

dir "%XALANCROOT%\Build\%XERCES_BUILD_ARCH%\%VCNAME_ALT%\%C%"

set PATH=%XERCESCROOT%\Build\%P%\%VCNAME_ALT%\%C%;%PATH%
::-First build only the MsgCreator project
set SLN=%WORKSPACE%\Projects\Win32\%VCNAME_ALT%\xalan.sln

::-Extract the appropriate Project files only for VS 2012 and above
if NOT "%T%"=="v100" (
	echo [INFO]Calling: %SEVENZ% x -aoa xalan\%VCNAME_ALT%.%ARCHIVE_EXTENSION% -o"%WORKSPACE%\projects\Win32"
	%SEVENZ% x -aoa xalan\%VCNAME_ALT%.%ARCHIVE_EXTENSION% -o"%WORKSPACE%\projects\Win32"
	if ERRORLEVEL 1 (
		echo [FAILURE]: Failed to extract xalan\%VCNAME_ALT%.%ARCHIVE_EXTENSION%
		exit /b ERRORLEVEL
	)
)	

echo [INFO]Calling "%MSVC.DIR%\VC\vcvarsall.bat" %VCVARS_C%
call "%MSVC.DIR%\VC\vcvarsall.bat" %VCVARS_C%
echo [INFO]Calling devenv "%SLN%" /build "%C%|%P%"
devenv "%SLN%" /build "%C%|%P%"
if ERRORLEVEL 1 (
	echo [FAILURE]Failed to build %SLN% using devenv
	exit /b ERRORLEVEL
)

::-Success archive the artifacts
set XALANC_BINARY_ARCHIVE=xalanc-%VERSION%-bin-%C%-%P%-%T%.%ARCHIVE_EXTENSION%
echo [INFO]Calling: %SEVENZ% a -t%ARCHIVE_EXTENSION% %XALANC_BINARY_ARCHIVE% -i!"Build/*/*/%C%/Xalan-C*.dll" -i!"Build/*/*/%C%/Xalan-C*.lib" -i!"Build/*/*/%C%/XalanMessages*.dll" -i!"Build/*/*/%C%/XalanMsgLib*.lib"
%SEVENZ% a -t%ARCHIVE_EXTENSION% %XALANC_BINARY_ARCHIVE% -i!"Build/*/*/%C%/Xalan-C*.dll" -i!"Build/*/*/%C%/Xalan-C*.lib" -i!"Build/*/*/%C%/XalanMessages*.dll" -i!"Build/*/*/%C%/XalanMsgLib*.lib"
if ERRORLEVEL 1 (
	echo [FAILURE]Failed to create archive %XALANC_BINARY_ARCHIVE%
	exit /b ERRORLEVEL
)

if "%T%"=="v120" (
	if "%P%"=="x64" (
		if "%C%"=="Release" (
			echo [INFO]Toolkit is %T%, Platform is %P% archiving the Xalan-C headers
			
			echo [INFO]Calling: %SEVENZ% a -t%ARCHIVE_EXTENSION% "xalanc-%VERSION%-inc.%ARCHIVE_EXTENSION%" -ir!src\xalanc -xr!src\*.svn -x!src\Makefile.*
			%SEVENZ% a -t%ARCHIVE_EXTENSION% "xalanc-%VERSION%-inc.%ARCHIVE_EXTENSION%" -ir!src\xalanc -xr!src\*.svn -x!src\Makefile.*
			
			echo [INFO]Calling: %SEVENZ% a -t%ARCHIVE_EXTENSION% "xalanc-%VERSION%-license.%ARCHIVE_EXTENSION%" -i!LICENSE
			%SEVENZ% a -t%ARCHIVE_EXTENSION% "xalanc-%VERSION%-license.%ARCHIVE_EXTENSION%" -i!LICENSE
		) else (
			echo [INFO]Toolkit is %T%, Platform is %P%, however Configuration is %C% hence skipping archiving the Xalan-C headers
		)
	) else (
		echo [INFO]Toolkit is %T%, however Platform is %P% hence skipping archiving the Xalan-C headers
	)
) else (
	echo [INFO]Toolkit is %T%, hence skipping archiving the Xalan-C headers
)

endlocal
